// https://www.shadertoy.com/view/4scSW4	
float displacement(vec3 p)
{
    vec2 d1 = p.xy * 0.3;
    vec2 d2 = d1 * 0.2;
    d1.x+=d1.y*2.;
    d1.x += iTime*0.05;
    d2.x += iTime *0.1;
    d2.y -= iTime * 0.025;
    d2.x += d2.y*2.;

    float h1 = texture(iChannel0, d2).x;
    float h2 = texture(iChannel0, d1).x;

    return h1 * .6 + h2 * 0.1* (1.0 - h1 );
}

vec2 map(vec3 p)
{
    vec2 r;


    float d = displacement(p);
    float warp = cos(p.y*.3*cos(iTime/10.+1.56));
    float column1 = sphere(p * warp,3.0) + d;

    r = vec2(column1, 1.0);
    return r ;
}


vec2 ray(vec3 ro, vec3 rd)
{
    float t=0.,m;
    for(int i=0;i<128;i++)
    {
        vec3 p = ro+rd*t;
        vec2 s = map(p);
        m=s.y;
        if(s.x<0.001)break;
        t+=s.x;
        if(t>MAX_DIST){t=-1.;break;}
    }
    return vec2(t,m);
}


vec3 normal(vec3 p)
{
    vec2 e = vec2(0.01,0);
    return normalize(vec3(
        map(p+e.xyy).x-map(p-e.xyy).x,
        map(p+e.yxy).x-map(p-e.yxy).x,
        map(p+e.yyx).x-map(p-e.yyx).x
        ));
}
float fresnel(vec3 n, vec3 dir, float power)
{
    return pow(1.0+dot(dir, n), power);
}
float shine(vec3 p, vec3 n)
{
    p.y = 0.;
    float d = (dot(normalize(p), n));

    float r = 0.0;
    if(d >= .99)
        r += 0.2*d;
    return r;
}
vec3 down(vec3 c, float s)
{
    c.rg*=s;
    c.rg = floor(c.rg);
    c.rg/=s;
    return c;
}

void mainImage( out vec4 c, in vec2 f)
{
    vec2 uv = (2.*f-iResolution.xy)/iResolution.y;

    vec3 col = vec3(0.067,0.502,0.933)*(((cos(uv.x)+cos(uv.y))))/2.75;
    col = down(col,60.);
    vec3 bg = col;
    float t = cos(iTime/5.)/3.;
    float d = 9.5+(1.0+cos(iTime/2.));
    vec3 ro = vec3(0,sin(t/.1),cos(t)*d);
    vec3 ta = vec3(0);
    vec3 rd = cam(ro,ta,uv);

    vec2 r = ray(ro,rd);

    if(r.x>0.0)
    {
        vec3 p = ro+rd*r.x;
        vec3 n = normal(p);


        vec3 mate=vec3(.0,.5,1.0);
        vec3 sun = normalize(vec3(0.5,.5,.5));
        vec3 backlight = normalize(vec3(-0.5,-.5,-.5));
        float dif = clamp(dot(sun,n),0.,1.);
        float bdif = clamp(dot(backlight,n),0.,1.);
        float sky = clamp(dot(n,vec3(0,1,0)),0.,1.);
        float bou = clamp(dot(n,vec3(0,-1,0)),0.,1.);
        float sha = clamp(ray(p+n*.001,sun).x,0.,1.);


        col=dif * mate;
        col+=bdif * mate;
        col+=sky * mate;
        col+=bou * mate;
        col += fresnel(n, rd, 3.) * vec3(0.6,0.9,1.0);

        vec3 ref = texture(iChannel1, n).rgb;
        float s = shine(p,n);
        col = mix(col, ref, 0.5 + s);
        col = mix(col, bg, fresnel(n,rd,2.)*1.5);
        col=down(col,8.);
        col = mix(col, bg, clamp(r.x/MAX_DIST,0.,1.));
    }



    c.rgb=col;
}

