#define R iResolution.xy
#define T (iTime)


float hash21(in vec2 ip, in float seed) {
    uvec2 p = floatBitsToUint(ip);
    uint s = floatBitsToUint(seed);
    uvec2 k = (~p >> 3U) * (s + 561U);
    uint j = ~s; 
    p ^= p << 17U;
    p ^= p >> 13U;
    p += (p.x * j + p.y);
    p ^= p << 5U; 
    p = (p + (p * k) * (p * s)) * 1013U;  
    return float((p.x*p.y)*5013U) / float(0xFFFFFFFFU);
}

float noise21(in vec2 p, in float seed) {
    vec2 lv = fract(p);
    lv = lv * lv * (3.0 - 2.0 * lv);
    vec2 id = floor(p);  
    return mix(
        mix(hash21(id, seed), hash21(id+vec2(1, 0), seed), lv.x),
        mix(hash21(id+vec2(0, 1), seed), hash21(id+vec2(1, 1), seed), lv.x),
        lv.y
    );
}

float noise21(in vec2 p, in float seed, in float freq, const in int oct) {
    float div = 0.0;
    float amp = 1.0;
    float n = 0.0;

    for (int i = 0; i < oct; i++) {
        n += amp * noise21(p*freq, seed);
        div += amp;
        amp /= 2.0;
        freq *= 2.0;
    }

    return n / div;
}

float line(in vec2 p, in vec2 a, in vec2 b, in float t) {
    vec2 pa = p - a;
    vec2 ba = b - a;
    float d = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    return clamp(1.0 - length(pa - ba * d) / t, 0.0, 1.0);
}


void mainImage( out vec4 O, in vec2 fc )
{
    vec2 uv = fc/R.y;
    vec3 col = vec3(0.0);
    vec2 dir = normalize(mix(vec2(0, -1), vec2(cos(T), sin(T)), 0.2));
    float len = 0.26;
    vec2 start = vec2((R.x/R.y)*0.5, 0.5);
    vec2 end = start+dir*len;
    float n0 = noise21(uv-dir, 0.34981, 3.0, 3);
    vec2 toff = vec2(sin(T+n0), cos(T+n0));
    float n = noise21(mix(
    uv+toff, (uv+toff)-end,
    distance(uv, start)/max(0.1, distance(uv, end))), 0.181813, 6.0, 3);
    vec2 off = vec2(cos(n*6.28), sin(n*6.28))*0.021;
    float lrp = smoothstep(0.02, 1.4, distance(uv + off, end));
    vec2 lp = mix(start, end, lrp);
    float l = line(uv, start, end, max(0.01, lrp));
    col += l;
    col = mix(col, col*vec3(1, 0, 0), smoothstep(0.0, 0.4, distance(uv + off, end)));
    col = mix(col, vec3(1.0, 0.9, 0.25), l*smoothstep(0.3, 1.0, distance(uv + off, end)));
    col = pow(col, vec3(1.0 / 2.2));
    O = vec4(col, 1.0);
}

