// generate lightmap using pathtracing

#define MAXLIGHT .3
#define MAXBOUNCES 2

//#define LIGHTINGONLY

vec3 shader0(vec2 uv) {
#ifdef LIGHTINGONLY
    return vec3(.7);
#else
    return texture(iChannel1, uv*2.).rgb;
#endif
}

vec3 shader1(vec2 uv) {
#ifdef LIGHTINGONLY
    return vec3(.7);
#else
    return texture(iChannel2, uv).rgb;
#endif
}

vec3 shader2(vec2 uv) {
#ifdef LIGHTINGONLY
    return vec3(.7);
#else
    return texture(iChannel3, uv*.5).rgb;
#endif
}

vec3 shadecol(int triangle, vec3 bary)
{
    Triangle t = unpackTriangle(triangle);
    vec2 uv = t.UVA*bary.x + t.UVB*bary.y + t.UVC*bary.z;
    if (t.MaterialID == 0) return shader0(uv);
    if (t.MaterialID == 1) return shader1(uv);
    if (t.MaterialID == 2) return shader2(uv);
    return vec3(1., 0., 1.);
}

vec3 tuv2wp(Triangle t, vec3 bary)
{
    return t.PointA*bary.x + t.PointB*bary.y + t.PointC*bary.z;
}

HitPoint trace(vec3 wp, vec3 wn, vec3 raydir, int triangle)
{
    HitPoint ret;
    ret.triangle = -1;
    ret.bary = vec3(-1.);
    ret.dist = -1.;

    int hit = -1;
    float hitdist = -1.;
    vec3 hitbary = vec3(0.);
    for (int j = 0; j < triangleLength(); j++) {
        if (j == triangle) continue;
        Triangle t = unpackTriangle(j);
        vec3 center = (((t.PointB - t.PointA)/2. + t.PointA) - t.PointC)/2. + t.PointC;
        vec3 normal = normalize((t.NormalA + t.NormalB + t.NormalC) * 0.5); // average normal of triangle

        // check if we hit plane
        float denom = dot(normal, raydir);
        if (!(abs(denom) > 0.0001f)) continue;

        float testt = dot((center - wp), normal) / denom;
        if (testt < 0.) continue;

        // if not inside triangle, continue
        vec3 b = bary3d(t.PointA, t.PointB, t.PointC, wp + testt*raydir);
        if (!baryinside(b))
            continue;

        if (hit >= 0) {
            if (hitdist < testt)
                continue;
            hit = j;
            hitdist = testt;
            hitbary = b;
        } else {
            hit = j;
            hitdist = testt; // distance from wp. hitpos = wp + hitdist*raydir
            hitbary = b;
        }
    }

    if (hit < 0)
        return ret;

    ret.triangle = hit;
    ret.bary = hitbary;
    ret.dist = hitdist;
    return ret;
}

vec4 tracerow(vec3 wp, vec3 wn, vec3 raydir, int triangle)
{
    vec3 color = vec3(.8, .9, 1.);
    float totaldist = -1.;
    vec3 rd = raydir;
    for (int i = 0; i < MAXBOUNCES; i++) {
        HitPoint h = trace(wp, wn, rd, triangle);
        if (h.triangle < 0) {
            //color *= vec3(1.); //vec3(1., 0., 1.); // sky
            totaldist += 50.;
            break;
        }
        color *= shadecol(h.triangle, h.bary); // * clamp(MAXLIGHT - h.dist, 0., 1.); // needs lighting model from distance
        totaldist += h.dist;
        wp = wp + rd*h.dist;
        triangle = h.triangle;
        wn = hit2n(h);
        rd = reflect(rd, wn);
    }

    return vec4(color, totaldist);

    // todo: trace in loop
    HitPoint h = trace(wp, wn, raydir, triangle);
    if (h.triangle < 0)
        return vec4(-1.);

    return vec4(shadecol(h.triangle, h.bary), h.dist);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    buildscene();

    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = texture(iChannel0, uv).rgb;

    vec3 thissample = col; // default to nop
    for (int i = 0; i < triangleLength(); i++) {
        Triangle t = unpackTriangle(i);

        float m = edge(t.LUVC, t.LUVB, uv);
        float n = edge(t.LUVA, t.LUVC, uv);
        float o = edge(t.LUVB, t.LUVA, uv);
        bool inside = m > 0. && n > 0. && o > 0.;
        if (!inside)
            continue;

        float area = edge(t.LUVB, t.LUVA, t.LUVC);
        vec3 bary = vec3(
            m/area, n/area, o/area
        );

        vec3 wp = tuv2wp(t, bary);
        vec3 wn = t.NormalA*bary.x + t.NormalB*bary.y + t.NormalC*bary.z;

        vec3 matcol = shadecol(i, bary);
        thissample = matcol;

        vec3 raydir = normalize(vrand(iTime + fract(wp.x+wp.y+wp.z)));
        if (dot(raydir, wn) < 0.)
            raydir *= -1.;

        vec4 result = tracerow(wp, wn, raydir, i);

        if (result.a < 0.)
            continue;

        thissample = result.rgb * matcol;
    }

    float m = clamp(pow(clamp((5.-iTime)/5., 0., 1.), 2.), 0.005, 1.);
    fragColor = vec4(mix(col, thissample, m), 1.0);
}

