Triangle unpackTriangle(int id)
{
    //return SceneTriangles[id];
    Triangle t;
    int offset = 31;
    int i = id*offset;

    t.PointA  = vec3(uintBitsToFloat(data[i   ]), uintBitsToFloat(data[i+ 1]), uintBitsToFloat(data[i+ 2]));
    t.PointB  = vec3(uintBitsToFloat(data[i+ 3]), uintBitsToFloat(data[i+ 4]), uintBitsToFloat(data[i+ 5]));
    t.PointC  = vec3(uintBitsToFloat(data[i+ 6]), uintBitsToFloat(data[i+ 7]), uintBitsToFloat(data[i+ 8]));
    t.NormalA = vec3(uintBitsToFloat(data[i+ 9]), uintBitsToFloat(data[i+10]), uintBitsToFloat(data[i+11]));
    t.NormalB = vec3(uintBitsToFloat(data[i+12]), uintBitsToFloat(data[i+13]), uintBitsToFloat(data[i+14]));
    t.NormalC = vec3(uintBitsToFloat(data[i+15]), uintBitsToFloat(data[i+16]), uintBitsToFloat(data[i+17]));
    t.MaterialID = int(data[i+18]);
    t.UVA  = vec2(uintBitsToFloat(data[i+19]), uintBitsToFloat(data[i+20]));
    t.UVB  = vec2(uintBitsToFloat(data[i+21]), uintBitsToFloat(data[i+22]));
    t.UVC  = vec2(uintBitsToFloat(data[i+23]), uintBitsToFloat(data[i+24]));
    t.LUVA = vec2(uintBitsToFloat(data[i+25]), uintBitsToFloat(data[i+26]));
    t.LUVB = vec2(uintBitsToFloat(data[i+27]), uintBitsToFloat(data[i+28]));
    t.LUVC = vec2(uintBitsToFloat(data[i+29]), uintBitsToFloat(data[i+30]));

    return t;
}

int triangleLength()
{
    //return SceneTriangles.length();
    return data.length() / 31;
}

struct HitPoint {
    int triangle;
    vec3 bary;
    float dist;
};

vec3 hit2n(HitPoint h)
{
    Triangle t = unpackTriangle(h.triangle);
    return normalize(t.NormalA*h.bary.x + t.NormalB*h.bary.y + t.NormalC*h.bary.z);
}

vec3 hit2w(HitPoint h)
{
    Triangle t = unpackTriangle(h.triangle);
    return t.PointA*h.bary.x + t.PointB*h.bary.y + t.PointC*h.bary.z;
}


