float edge(vec2 v0, vec2 v1, vec2 p) {
    return (p.x-v0.x)*(v1.y-v0.y)-(p.y-v0.y)*(v1.x-v0.x);
}

// from https://math.stackexchange.com/questions/544946/determine-if-projection-of-3d-point-onto-plane-is-within-a-triangle/544947
vec3 bary3d(vec3 A, vec3 B, vec3 C, vec3 P)
{
    vec3 u = B - A;
    vec3 v = C - A;
    vec3 n = cross(u, v);
    vec3 w = P - A;

    float a = dot(n, n);

    float gamma = dot(cross(u, w), n) / a;
    float beta  = dot(cross(w, v), n) / a;
    float alpha = 1. - gamma - beta;
    return vec3(alpha, beta, gamma);
}

bool baryinside(vec3 b)
{
    return b.x >= 0. && b.x <= 1.
        && b.y >= 0. && b.y <= 1.
        && b.z >= 0. && b.z <= 1.;
}

void calclightmap(int tid, float scale, vec2 offset)
{
    Triangle t = SceneTriangles[tid];
    SceneTriangles[tid].LUVA = t.UVA*scale + offset;
    SceneTriangles[tid].LUVB = t.UVB*scale + offset;
    SceneTriangles[tid].LUVC = t.UVC*scale + offset;
}

void addplane(int tid, vec3 v1, vec3 v2, vec3 v3, vec3 v4, float scale, vec2 offset, int mat)
{
    vec3 t1n = normalize(cross(v2-v1, v3-v1));
    vec3 t2n = normalize(cross(v3-v1, v4-v1));
    SceneTriangles[tid] = Triangle(
        v1,
        v2,
        v3,
        t1n,
        t1n,
        t1n,
        mat,
        vec2(0., 0.),
        vec2(1., 0.),
        vec2(1., 1.),
        vec2(0.),
        vec2(0.),
        vec2(0.)
    );
    SceneTriangles[tid+1] = Triangle(
        v1,
        v3,
        v4,
        t2n,
        t2n,
        t2n,
        mat,
        vec2(0., 0.),
        vec2(1., 1.),
        vec2(0., 1.),
        vec2(0.),
        vec2(0.),
        vec2(0.)
    );
    calclightmap(tid, scale, offset);
    calclightmap(tid+1, scale, offset);
}

void addbox(int tid, vec3 p, vec3 q, float scale, vec2 offset, int mat)
{
    vec3 v1, v2, v3, v4;

    // x-
    v1 = p;
    v2 = vec3(q.x, p.y, p.z);
    v3 = vec3(q.x, p.y, q.z);
    v4 = vec3(p.x, p.y, q.z);
    addplane(tid, v1, v2, v3, v4, scale/2., offset, mat);

    // y-
    v1 = vec3(p.x, q.y, p.z);
    v2 = p;
    v3 = vec3(p.x, p.y, q.z);
    v4 = vec3(p.x, q.y, q.z);
    addplane(tid+2, v1, v2, v3, v4, scale/2., offset+offset*vec2(scale/2., 0.), mat);
}

void buildscene()
{
    addplane(0, vec3(0.), vec3(1., 0., 0.), vec3(1., 1., 0.), vec3(0., 1., 0.), 0.5, vec2(0.), 1);
    addplane(2, vec3(.5, .4, 0.), vec3(.7, .4, 0.), vec3(.7, .4, .2), vec3(.5, .4, .2), .25, vec2(.5, 0.), 2);
    addplane(4, vec3(.5, .6, 0.), vec3(.5, .4, 0.), vec3(.5, .4, .2), vec3(.5, .6, .2), .25, vec2(.75, 0.), 3);

    //addbox(4, vec3(.5, .7, 0.), vec3(.7, .9, .2), .1, vec2(0., .5), 2);
}

