// raytrace and composite final colors

vec3 rotation = vec3(0.);
vec3 rotate(vec3 p)
{
    mat3 yaw = mat3(
        cos(rotation.x), -sin(rotation.x), 0.,
        sin(rotation.x),  cos(rotation.x), 0.,
        0.      ,  0.      , 1.
    );
    mat3 pitch = mat3(
        1.,       0.,        0.,
        0., cos(rotation.y), -sin(rotation.y),
        0., sin(rotation.y),  cos(rotation.y)
    );
    mat3 roll = mat3(
         cos(rotation.z), 0., sin(rotation.z),
               0., 1.,       0.,
        -sin(rotation.z), 0., cos(rotation.z)
    );

    p *= roll;
    p *= pitch;
    p *= yaw;
    return normalize(p);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 ouv = uv;

    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec3 lm = texture(iChannel0, uv).rgb;

    uv -= 0.5;
    uv.y *= iResolution.y/iResolution.x;

    float ts = iTime*.3;

    vec3 ro = vec3(sin(ts)*.2-.7, -2.2, 1.3);
    rotation = vec3(-0.3-cos(ts)*.1, -.6, 0.);
    vec2 randr = vec2(frand(float(iFrame+3)), frand(float(iFrame+5)))/iResolution.xy/3.;
    vec3 rd = rotate(normalize(vec3(uv.x+randr.x, 1., uv.y+randr.y)));

    bool validfragment = false;

    vec3 color = vec3(0.);

    buildscene();

    int hit = -1;
    float hitdist;
    vec3 hitbary;
    for (int i = 0; i < triangleLength(); i++) {
        Triangle t = unpackTriangle(i);

        vec3 center = (((t.PointB - t.PointA)/2. + t.PointA) - t.PointC)/2. + t.PointC;
        vec3 normal = normalize((t.NormalA + t.NormalB + t.NormalC) * 0.5); // average normal of triangle

        // check if we hit plane
        float denom = dot(normal, rd);
        if (!(abs(denom) > 0.0001f)) continue;

        float testt = dot((center - ro), normal) / denom;
        if (testt < 0.) continue;

        vec3 b = bary3d(t.PointA, t.PointB, t.PointC, ro + testt*rd);
        if (!baryinside(b))
            continue;

        if (hit >= 0) {
            if (hitdist < testt)
                continue;
            hit = i;
            hitdist = testt;
            hitbary = b;
        } else {
            hit = i;
            hitdist = testt; // distance from wp. hitpos = wp + hitdist*raydir
            hitbary = b;
        }
    }

    if (hit < 0)
        color = vec3(0.);
    else {
        Triangle t = unpackTriangle(hit);
        vec3 hitpos = ro + hitdist*rd;
        vec2 luv = t.LUVA*hitbary.x + t.LUVB*hitbary.y + t.LUVC*hitbary.z;
        color = texture(iChannel0, luv).rgb;
    }

    fragColor = mix(texture(iChannel1, ouv), vec4(color, hitdist), 0.4);
}

