#define TEXT0(x) texture(iChannel0, vec2(x))


#define R1X int(iChannelResolution[1].x)
#define XY2ID(X) (int(X.y) * R1X + int(X.x))
#define ID2XY(I) ivec2(I - I / R1X * R1X, I / R1X)
#define GET1(I) texelFetch(iChannel1, ID2XY(I), 0)
#define GETR1 mat3(GET1(1).xyz, GET1(2).xyz, GET1(3).xyz)

const float pi = 4. * atan(1.);

vec3 vu2xyz(vec2 uv){
    float l = dot(uv,uv);
    return l>1.?vec3(uv/l,-sqrt(1. - 1./l)):vec3(uv,sqrt(1. - l));
}

