    return fract((p3.x + p3.y) * p3.z);
}


float closestWall(vec3 p) {
    vec3 d = min(fract(p), 1.0 - fract(p));
    return min(min(d.x, d.y), d.z);
}

float box (vec3 p, vec3 s) {
    p = abs(p) - s;
    return max(max(p.x,p.y),p.z);
}
float map (vec3 p){


   // geo
   float tt = iTime;

   vec3 id = floor(p)-.5; 
   p = fract(p) -.5;

   float f1 = hash31(id);
   float f2 = sin(iTime) * .5+ .5;
   float f3 = step(f1,f2);

   float box = box(p, vec3(.3) *f3);
   float bound = closestWall(p+.5);

   return min(bound + .01, box);
}

vec3 norm(vec3 p) {
  vec2 off=vec2(0.001,0.0);
  return normalize(map(p)-vec3(map(p-off.xyy), map(p-off.yxy), map(p-off.yyx))+0.000001);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);

  float tt = iTime * 2.;

  // nav

  vec3 s,t;

  s = lattice(tt*.1) * 10.;
  t = lattice(tt *.1 + 2.) * 18.;


  vec3 cz=normalize(t-s);
  vec3 cx=normalize(cross(cz,vec3(0,1,0)));
  vec3 cy=-normalize(cross(cz,cx));
  vec3 r=normalize(cx*uv.x+cy*uv.y+cz);


  vec3 p=s;
  float i=0.;
  float dd = 0.;
  float d;

  // march

  bool hit = false;
  for(float z=0.; z < 500.; ++z) {
    i = z;
    d=map(p);
    if(d<0.001) {
      hit = true;
      break;
    }
    if ( dd > 1000.) break;
    dd += d;
    p+=r*d;
  }


  // color

  vec3 n = norm(p);
  vec3 light =normalize(vec3(0,1,-1));
  float fresnel = pow(1.-dot(n, -r),1.)* .9 + .1;
  float spec =pow(max(dot(reflect(-light,n),-r),0.),40.);
  vec3 hue = vec3(1.);
  vec3 col = hue  *  (fresnel + spec);

  if ( ! hit ) {
      col = vec3(.2,.2,.5);
  }

  fragColor = vec4(col, 1);
}

