void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor -= fragColor;       
    vec2 UV =(fragCoord+fragCoord-iResolution.xy)/iResolution.y;

    float ct = smoothstep(0.,0.7,sin(iTime + 2.)+.7);

    // Outer Glow
    float dist = (pow(1.-length(UV)/2.,2.)) * ct;
    fragColor = vec4(dist);

    // Outer Box    
    float size = 5.5 * ct;
    //float size = 5.5;
    float outer = sdBox(UV,vec2(1.,1.))*size;
    outer = mod(2., outer+.01);
    fragColor = max(fragColor,vec4(1.-step(-1.,outer)));

    // Mid Angled Box
    vec2 rUV = rotate(PI*2.*ct + PI/4.) * UV;
    //vec2 rUV = rotate(PI/4.)*UV;
    float p = sdBox(rUV, vec2(.84));
    float mid = distance(p+.2, .0);
    mid = step(.03,fract(mid-.03));
    mid = 1.-mid;
    fragColor = vec4(mix(mid, fragColor.x, step(.0,p+.14)));

    // Inner details
    vec2 tUV = UV * ct;    
    float inner = smoothstep(0.675,0.68,length(tUV));
    inner = (1. - inner) * smoothstep(0.645,0.65,length(tUV));
    inner = max(inner, 1.-smoothstep(0.445,0.45,length(tUV)));
    inner = min(inner, smoothstep(0.245,0.25,length(tUV)));    
    //inner = mix(inner,fragColor.x,step(0.,p+.26));  // Clip inside of angled box

    // Electra
    float elec = texture(iChannel0,((fragCoord/iResolution.xy) - vec2(0.0833, -0.01))*1.2).x;

    // Smoothing Electra outline
    float mask = sdBox(UV+vec2(0.,.3), vec2(.127,.2));
    float ring = 1.-smoothstep(0.415,0.42,length(UV));
    ring = max(ring, 1.-smoothstep(0.315,0.62,length(UV)));   // Inner Circle Glow
    ring *= smoothstep(0.395,0.40,length(UV));
    ring *= mix(0., ring, step(0.02, mask));

    elec = max(elec, ring);    

    inner = mix(elec, inner,smoothstep(.352,.347,(1.-length(UV))*ct));     

    fragColor = vec4(mix(inner,fragColor.x,step(.0,p+.26)));

    // Colour
    vec3 col = vec3(1.);

    fragColor = fragColor * vec4(col, 1.) ;

    //fragColor = vec4(inner);
    //fragColor = vec4(fract(((fragCoord/iResolution.xy) - .14)*1.4), 0.,1.);
}

