float RenderFont_NoAA
    (
        vec2 uv,
        Typeface font,
        uint text[8]
    )
{
    // each char is in a 64x64 space, trim off the white boundary
    vec2 charBottomLeft = vec2(18,3) - font.padding/2.;
    vec2 charDims = vec2(28,52) + font.padding;

    uv = uv/font.scale; //+vec2(iTime*200.,0);

    if ( uv.y < .0 || uv.y >= charDims.y ) return 1.;

    uint index = uint(uv.x/charDims.x);
    ivec2 res = ivec2(iResolution.xy);
    uint index4 = index/4u;

    if ( index4 >= uint(text.length()) ) return 1.;

    uint char = ( text[index4] >> (8u*(index&3u)) )&0xffu;

    char = char^0xf0u; // flip the y coord

    uv.x = fract(uv.x/charDims.x)*charDims.x;
    uv += vec2(ivec2(char&0xfu,char>>4u)) * iChannelResolution[0].xy/16.;
    uv += charBottomLeft;

    vec4 v = texture( iChannel0, uv/iChannelResolution[0].xy ) -.5;

    vec2 bias = 2.*v.yz;
    bias = bias*cos(font.biasAngle) + bias.yx*sin(font.biasAngle)*vec2(1,-1);
    bias = pow(abs(bias),vec2(3));

    // view the SDF
//    float f = v.w + dot( font.bias, bias ) - font.threshold; return .5+.5*f/(abs(f)+.04);

    return step( font.threshold, v.w + dot( font.bias, bias ) );
}



// RenderFont
//
// Output:
//		A linear brightness value where 0 = text, 1 = background.
//		I recommend applying a gamma curve before displaying.
//
// uv
//		Relative to the bottom left of the line of text, most fonts are about 40 units tall
//
// font
//		One of the Typefaces defined earlier
//
// text
//		A line of text, encoded using my tool here: https://tekf.github.io/Misc-Tools/StringToHex.html
//		Each byte is 4-bit u, 4-bit v coord of a character in the font texture.
//
float RenderFont
    (
        vec2 uv,
        Typeface font,
		uint text[8]
    )
{
    const int numSamples = 1<<(AA_QUALITY);

    float sum = 0.;
    vec2 duvdx = dFdx(uv);
    vec2 duvdy = dFdy(uv);

    // anti-alias the font
    // this would be more efficient run per-character inside the font render function
    // (the SDFs on some of the fonts are good enough that I could probably just soften the step function)
    for ( int i=0; i < numSamples; i++ )
    {
        // ideal 2D quasirandom sequence from http://extremelearning.com.au/unreasonable-effectiveness-of-quasirandom-sequences/
        uvec2 quasi2 = uvec2(0xC13FA9A9u,0x91E10DA5u);
        uint seed = uint(i);
        //seed += uint(iFrame*numSamples); // randomize per frame - causes shimmering
        //seed += uint(fragCoord.x)*quasi2.x+uint(fragCoord.y)*quasi2.y; // randomize per pixel - this looks bad at low sample counts (and at high counts it's less important)
        vec2 jitter = vec2( quasi2 * seed ) / exp2(32.);

        if ( AA_ROUND )
        {
            // circle of confusion slightly bigger than a pixel - should look more photographic
            jitter.x *= 6.283185;
            jitter = AA_ROUND_RADIUS*(1.-jitter.y*jitter.y)*vec2(cos(jitter.x),sin(jitter.x));
        }
        else
        {
            jitter -= .5;
        }

        sum += RenderFont_NoAA( uv + jitter.x*duvdx + jitter.y*duvdy, font, text );
    }

    return sum/float(numSamples);
}



