void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float cellsize = 32.0;
    vec2 uv = fragCoord/iResolution.xy;

    vec2 divpos = floor(fragCoord / cellsize);
    vec2 modpos = mod(fragCoord, cellsize) - (cellsize * 0.5);

    float rot = iTime*4.0 + sin(divpos.x*0.5) + sin(divpos.y*0.5) + divpos.x*0.4;
    vec2 offset = vec2(sin(rot)*10.0, cos(rot)*10.0);
    modpos += offset;

    float c = 1.0 - sqrt(modpos.x*modpos.x + modpos.y*modpos.y) * 0.5;

    // Output to screen
    fragColor = vec4(c,c,c,1.0);
}

float hash(float x)
{
	return fract(sin(cos(x*12.13)*19.123)*17.321);
}


float noise(vec2 p)
{
	vec2 pm = mod(p,1.0);
	vec2 pd = p-pm;
	float v0=hash(pd.x+pd.y*41.0);
	float v1=hash(pd.x+1.0+pd.y*41.0);
	float v2=hash(pd.x+pd.y*41.0+41.0);
	float v3=hash(pd.x+pd.y*41.0+42.0);
	v0 = mix(v0,v1,smoothstep(0.0,1.0,pm.x));
	v2 = mix(v2,v3,smoothstep(0.0,1.0,pm.x));
	return mix(v0,v2,smoothstep(0.0,1.0,pm.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float shift = ((1.0 - uv.x) * 12.0);

    // Time varying pixel color
    //float v = mod((iTime / pow(2.0, shift)) + uv.y + hash(uv.x)+hash(uv.x*3.0)+hash(uv.x*69.0), 1.0) > 0.5 ? 0.0 : 1.0;
    //v = mod(v+hash(uv.x)+hash(uv.x*3.0)+hash(uv.x*69.0), 1.0);
    float col = mod((iTime / exp2(shift) * 16.0) + uv.y, 1.0);

    // fractal noise yoinked from https://www.shadertoy.com/view/Msf3Wr by mu6k
    float v =0.0;
    float octaves = 24.0;
    for (float i = 0.0; i<octaves; i+=1.0)
	{
		float t = i;
		float l = t;
		float e = exp2(t);
		v+=noise(vec2(uv.x, mod(uv.y+col+hash(uv.x)+hash(uv.x*3.0)+hash(uv.x*69.0), 1.0))*e+vec2(cos(l)*53.0,sin(l)*100.0))*(1.0-(t/octaves))*(t/octaves);

	}

	v-=0.5;
    v *= 0.5;

    // Output to screen
    //fragColor = vec4((uv.y > 0.498 && uv.y < 0.502) ? vec3(0.0) : vec3(v),1.0);
    fragColor = vec4(vec3(v)*vec3(0.3, 1.0, 0.3), 1.0);
}

vec2 init(vec2 fragCoord, vec2 res) {
    float ratio = res.y/res.x;
    vec2 mr = vec2(min(res.x, res.y));
    vec2 uv = (fragCoord/mr) + vec2(-0.5/ratio, -0.5);
    return uv;
}

vec2 sq(vec2 c) {
    float re = c.x*c.x - c.y*c.y;
    float im = 2.*c.x*c.y;
    return vec2(re,im);
}

float amount(vec2 c) {
    return sqrt(c.x*c.x + c.y*c.y);
}

int mandel(vec2 c0, vec2 c00, int maxIter) {
    vec2 c = sq(c0);

    int iter;
    for (iter=0;iter < maxIter && amount(c) < 4.; iter++) {
        c = sq(c - c00);
    }

    return iter;
}

float band(int hz) { 
    int ix = int(float(hz)/23.); 
    float fft  = texelFetch( iChannel0, ivec2(ix,0), 0 ).x; 
    return fft;
}


float sinp(float x) {
    return (1. + sin(x)) / 2.;
}

float sinp(int n) {
    return sinp(float(n));
}

float nn(int n) {
    if (n < 8) return float(n);
    return float(n) + band(180)*250.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = init(fragCoord,iResolution.xy*.4) - vec2(1.3, 0.8);

    int N = 10000;

    float ra = band(200)*.1;//sin(iTime * 1.012) * 0.02;
    float rb = band(200)*(.35-band(4000))/.6;//sin(iTime * 1.037) * 0.08;


    int n = mandel(uv.yx, vec2(.57+ra, .57+rb), N);


    vec3 col = n == N ? vec3(0) : vec3(
        sinp(nn(n) / 43.), sinp(nn(n) / 27.), sinp(nn(n) * .2)
    );

    fragColor = vec4(col,1.0);
}

