   idGard = 14, idMot = 15, idSup = 16, idAnt = 17, idSkd = 18, idCam = 19, idProp = 20;
const float pi = 3.1415927, sqrt3 = 1.7320508;

#define CosSin(x) (sin ((x) + vec2 (0.5 * pi, 0.)))
#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }
#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float FlObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, a, s, r;
  dMin = dstFar / flSz;
  p = flMat * (p - flPos) / flSz;
  p.xz = vec2 (p.z, - p.x);
  q = p;
  q.y -= 0.6;
  d = max ((abs (q.y) + abs (q.z)) / sqrt(2.) - 0.5, max (q.y - 0.35, abs (q.x) - 0.7));
  DMINQ (idCab);
  d = PrRoundCylDf (vec3 (q.x, q.y - 0.17, abs (q.z) - 0.53).yzx, 0.03, 0.07, 0.5);
  DMINQ (idCab);
  d = min (min (PrSphDf (vec3 (abs (q.x) - 0.7, q.y, q.z), 0.2),
     PrSphDf (vec3 (q.x - 0.7, q.y, abs (q.z) - 0.4), 0.1)),
     PrSphDf (vec3 (q.x, q.y - 0.35, q.z), 0.15));
  DMINQ (idCam);
  d = PrRoundCylDf (vec3 (abs (q.xz) - vec2 (0.5, 0.2), q.y - 0.6), 0.02, 0.01, 0.3);
  DMINQ (idAnt);
  q = p;
  d = PrSphDf (vec3 (abs (q.x) - 0.6, q.y + 0.35, q.z), 0.15);
  DMINQ (idCam);
  q.y -= -0.6;
  d = min (PrRoundBoxDf (vec3 (q.x, q.y, abs (q.z) - 0.55), vec3 (0.7, 0.01, 0.06), 0.02),
     PrCylDf (vec3 (abs (q.xz) - vec2 (0.55, 0.55), q.y - 0.2), 0.04, 0.2));
  DMINQ (idSkd);
  q = p;
  a = (length (q.xz) > 0.) ? atan (q.z, - q.x) / (2. * pi) : 0.;
  s = sign (fract (3. * a) - 0.5);
  q.xz = Rot2Cs (q.xz, CosSin (2. * pi * (floor (6. * a) + 0.5) / 6.));
  d = min (max ((abs (q.y) - q.x) / sqrt(2.) - 0.85, max (-1. - q.x, abs (q.y) - 0.35)),
     max ((abs (q.y) + abs (q.z)) / sqrt(2.) - 0.14, abs (q.x + 1.) - 0.1));
  DMINQ (idBod);
  d = max ((abs (abs (q.y) - 0.1) + abs (q.z)) / sqrt(2.) - 0.04, abs (q.x + 2.5) - 1.5);
  DMINQ (idArm);
  d = (abs (length (q.xz) - 3.06) + abs (abs (q.y) - 0.22)) / sqrt(2.) - 0.04;
  q.x += 2.8;
  r = length (q.xz) - 1.2;
  d = max (d, - r);
  DMINQ (idSup);
  d = PrRoundCylDf (q.xzy, 0.15, 0.05, 0.25);
  DMINQ (idMot);
  q.y = abs (q.y) - 0.22;
  d = max (Maxv2 (abs (abs (Rot2Cs (q.xz, CosSin (pi/4.))) - 0.85)) - 0.04, q.y);
  DMINQ (idSup);
  d = (abs (r) + abs (q.y)) / sqrt(2.) - 0.04;
  DMINQ (idGard);
  q.xz = Rot2Cs (q.xz, CosSin (10.1 * s * pi * tCur));
  d = PrRoundBoxDf (q, vec3 (1.1, 0.01, 0.07), 0.01);
  DMINQ (idProp);
  return flSz * dMin;
}

float FlObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = FlObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0005 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 FlObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = 1e-3 * vec2 (1., -1.);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = FlObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float FlObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = FlObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.1 * d, h));
    d += h;
    if (sh < 0.05 || d > 10.) break;
  }
  return 0.6 + 0.4 * sh;
}

