  } else if (idObj == idBod) {
    col4 = mix (gCol4, fCol4 * ((qHit.y < 0.) ? 1.2 : 1.), step (0.02, abs (qHit.y)));
    if (qHit.y < 0.) col4 = mix (lCol4.grba, col4, step (0.02, abs (qHit.x + 0.3)));
  } else if (idObj == idMot) {
    col4 = gCol4;
    if (abs (qHit.y) < 0.18) col4 *= 0.5 + 0.5 * step (0.5, fract (16. * a));
    else col4 = mix (lCol4.grba, col4, step (0.03, length (qHit.xz)));
  } else if (idObj == idArm) {
    col4 = mix (fCol4, gCol4, step (0.7, fract (8. * qHit.x)));
  } else if (idObj == idSup) {
    col4 = mix (fCol4, gCol4, step (0.7, fract (16. * qHit.y)));
  } else if (idObj == idGard) {
    col4 = mix (fCol4, gCol4, step (0.7, fract (64. * a)));
  } else if (idObj == idAnt) {
    col4 = vec4 (0.8, 0.8, 0.8, 0.2);
  } else if (idObj == idSkd) {
    col4 = vec4 (0.4, 0.4, 0.4, 0.05);
  } else if (idObj == idCam) {
    col4 = vec4 (0.1, 0.1, 0.1, 0.3);
  } else if (idObj == idProp)  {
    col4 = vec4 (0.9, 0.9, 1., 0.3) * (0.5 + 0.5 * step (0.5, fract (2. * abs (qHit.x))));
  }
  return col4;
}

float Ddot (vec2 p)
{
  return dot (p, p);
}

vec3 TruchSDist (vec2 p)
{
  vec2 pc, pc1, pc2, dp;
  float d, d1, d2, cxy, rc, ac, s, ss;
  bool ct;
  ct = (cType == 2. || cType == 4.);
  if (ct) {
    pc1 = - vec2 (0., cDir);
    pc2 = vec2 (sqrt3/2. * Ssign (p.x), 0.5 * cDir);
    d1 = Ddot (p - pc1);
    d2 = Ddot (p - pc2);
    d = min (d1, d2);
    pc = (d == d1) ? pc1 : pc2;
    rc = 0.5;
    d = abs (sqrt (d) - rc);
  } else {
    if (cDir != 0.) p = 0.5 * vec2 (p.x - cDir * sqrt3 * p.y, cDir * sqrt3 * p.x + p.y);
    pc1 = vec2 (sqrt3/2. * Ssign (p.x), 0.);
    pc2 = vec2 (sqrt3 * Ssign (p.x), 0.);
    d1 = sqrt (Ddot (p - pc1));
    d2 = abs (sqrt (Ddot (p - pc2)) - 1.5);
    d = min (d1, d2);
    pc = (d == d1) ? pc1 : pc2;
    rc = (d == d1) ? 0.: 1.5;
  }
  dp = p - pc;
  cxy = cIdB.x - cIdB.y;
  s = (ct && cxy < 0. || ! ct && abs (cxy - 2.) == 1.) ? -1. : 1.;
  ac = (0.5 - atan (dp.y, - dp.x) / (2. * pi)) * s;
  if (! ct && abs (cxy - 2.) <= 1.) ac += 1./6.;
  ss = Ssign (length (dp) - rc);
  return vec3 (d * ss, rc * ss * s, ac);
}

bool OnTrk (vec2 w)
{
  vec2 wp, wm;
  float cxy;
  bool cyo, offTrk;
  cxy = cIdB.x - cIdB.y;
  cyo = (mod (cIdB.y, 2.) == 1.);
  wm = Rot2Cs (w, sin (- pi / 3. + vec2 (0.5 * pi, 0.))) - vec2 (0., 0.3);
  wp = Rot2Cs (w, sin (pi / 3. + vec2 (0.5 * pi, 0.)));
  offTrk = (cxy == -2. && wm.y > 0. ||
     ! cyo && (cxy == -3. && w.y > -0.3 || cxy == -2. || cxy == -1. && wm.y > 0. ||
     cxy == 1. && wm.y < 0. || (cxy == 2. || cxy == 3.) && w.x < 0. || cxy == 4. || cxy == 5.) ||
     cyo && (cxy == -3. || cxy == 0. && wp.x > 0. || (cxy == 1. || cxy == 2.) && w.x > 0. ||
     cxy == 3. || cxy == 4. && wm.x < 0. || cxy == 5. && wm.y < 0.));
     return ! offTrk;
}

void BConf ()
{
  float h, cxy;
  cMidB = HexToPix (cIdB * hgSizeB);
  h = Hashfv2 (cIdB);
  if (Hashfv2 (17.11 * cIdB) > 0.4) {
    cDir = floor (3. * h) - 1.;
    cType = 3.;
  } else {
    cDir = 2. * floor (2. * h) - 1.;
    cType = 4.;
  }
  cxy = cIdB.x - cIdB.y;
  if (cxy == 0.) cType = 1.;
  else if (abs (cxy) == 1.) cType = mod (cIdB.x, 2.) + 1.;
  else if (cxy == -2. || cxy == -3. || cxy == 5.) cType = 2.;
  else if (cxy == 2. || cxy == 3. || cxy == 4.) cType = 1.;
  if (cType <= 2.) {
    if (cType == 1. && (cxy == 1. || cxy == 2. || cxy == 3.)) cDir = 0.;
    else if (cType == 1. && cxy == 4.) cDir = -1.;
    else if (cType == 2. && cxy == 5.) cDir = 1.;
    else cDir = 2. * mod (cIdB.x, 2.) - 1.;
  }
}

float BObjDf (vec3 p)
{
  vec3 q, cm3;
  vec2 w;
  float dMin, d;
  dMin = dstFar;
  if (cType > 0.) {
    q = p;
    d = q.y - 0.02;
    DMIN (idGrnd);
    w = (q.xz - cMidB) / hgSizeB;
    cm3 = TruchSDist (w);
    if (cType <= 2. && cm3.y != 0. && OnTrk (w)) {
      d = PrTorusBxDf (vec3 (hgSizeB * abs (cm3.x), q.y - 0.6, hgSizeB * HexEdgeDist (w)),
         vec3 (pWid, 0.6, 0.2), 0.05);
      DMIN (idFrm);
    }
  }
  return dMin;
}

float BObjRay (vec3 ro, vec3 rd)
{
  vec3 vri, vf, hv;
  vec2 edN[3], pM;
  float dHit, d, s, eps;
  bool cNu;
  if (rd.x == 0.) rd.x = 1e-3;
  if (rd.z == 0.) rd.z = 1e-3;
  eps = 0.002;
  edN[0] = vec2 (1., 0.);
  edN[1] = 0.5 * vec2 (1., sqrt3);
  edN[2] = 0.5 * vec2 (1., - sqrt3);
  for (int k = 0; k < 3; k ++) edN[k] *= Ssign (dot (edN[k], rd.xz));
  vri = hgSizeB / vec3 (dot (rd.xz, edN[0]), dot (rd.xz, edN[1]), dot (rd.xz, edN[2]));
  vf = 0.5 * sqrt3 - vec3 (dot (ro.xz, edN[0]), dot (ro.xz, edN[1]), dot (ro.xz, edN[2])) / hgSizeB;
  dHit = 0.;
  cIdB = PixToHex (ro.xz / hgSizeB);
  pM = HexToPix (cIdB);
  cNu = true;
  for (int j = VAR_ZERO; j < 120; j ++) {
    if (cNu) {
      hv = (vf + vec3 (dot (pM, edN[0]), dot (pM, edN[1]), dot (pM, edN[2]))) * vri;
      s = Minv3 (hv);
      BConf ();
      cNu = false;
    }
    d = BObjDf (ro + dHit * rd);
    if (dHit + d < s) {
      dHit += d;
    } else {
      dHit = s + eps;
      cIdB = PixToHex ((ro.xz + dHit * rd.xz) / hgSizeB);
      pM += sqrt3 * ((s == hv.x) ? edN[0] : ((s == hv.y) ? edN[1] : edN[2]));
      cNu = true;
    }
    if (d < eps || dHit > dstFar) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 BObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = 1e-3 * vec2 (1., -1.);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = BObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float BObjSShadow (vec3 ro, vec3 rd)
{
  vec3 p;
  vec2 cIdP;
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  cIdP = vec2 (-999.);
  for (int j = VAR_ZERO; j < 30; j ++) {
    p = ro + d * rd;
    cIdB = PixToHex (p.xz / hgSizeB);
    if (cIdB != cIdP) {
      cIdP = cIdB;
      BConf ();
    }
    h = BObjDf (p);
    sh = min (sh, smoothstep (0., 0.1 * d, h));
    d += h;
    if (sh < 0.05 || d > 10.) break;
  }
  return 0.6 + 0.4 * sh;
}

vec4 BObjCol (vec3 ro, out vec2 vf)
{
  vec4 col4;
  vec3 col, cm3;
  vec2 w;
  float dt;
  vf = vec2 (0.);
  if (idObj == idGrnd) {
    w = (ro.xz - cMidB) / hgSizeB;
    cm3 = TruchSDist (w);
    col = mix (vec3 (0.4, 0.3, 0.1), vec3 (0.4, 0.5, 0.2), 2. * Fbm2 (ro.xz));
    vf = vec2 (8., 1.);
    if (cType <= 2. && cm3.y != 0. && OnTrk (w)) {
      dt = hgSizeB * abs (cm3.x);
      col = mix (vec3 (0.6, 0.6, 0.5), col, smoothstep (0., 0.01, abs (dt - pWid) - 0.03));
      if (dt < pWid) {
        col = vec3 (0.5, 0.4, 0.2) * (1. - 0.1 * Noisefv2 (16. * ro.xz));
        vf = vec2 (64., 0.2);
      }
    }
    col4 = vec4 (col, 0.);
  } else if (idObj == idFrm) {
    col4 = (ro.y > 0.05) ? vec4 (0.8, 0.6, 0.2, -1.) : vec4 (0.7, 0.7, 0.8, 0.);
  }
  return col4;
}

vec3 BgCol (vec3 ro, vec3 rd)
{
  vec3 col, clCol;
  vec2 q;
  float f, ff, fd;
  if (rd.y > -0.02 && rd.y < 0.03 * Fbm1 (16. * atan (rd.z, - rd.x))) {
     col = mix (vec3 (0.4, 0.5, 0.8) + 0.05, vec3 (0.3, 0.41, 0.65) + 0.07,
        smoothstep (-0.02, 0.01, rd.y));
  } else {
    q = 0.02 * (ro.xz + 0.5 * tCur + ((50. - ro.y) / rd.y) * rd.xz);
    ff = Fbm2 (q);
    f = smoothstep (0.2, 0.8, ff);
    fd = smoothstep (0.2, 0.8, Fbm2 (q + 0.01 * sunDir.xz)) - f;
    clCol = (0.7 + 0.5 * ff) * (vec3 (0.7) - 0.7 * vec3 (0.3, 0.3, 0.2) * sign (fd) *
       smoothstep (0., 0.05, abs (fd)));
    col = mix (vec3 (0.4, 0.5, 0.8), clCol, 0.1 + 0.9 * f * smoothstep (0.02, 0.05, rd.y));
  }
  return col;
}

void SConf ()
{
  vec2 r;
  cMidS = HexToPix (cIdS * hgSizeS);
  r = Hashv2v2 (73. * cIdS + 1.1);
  tOff.xz = 0.26 * sqrt3 * hgSizeS * (0.5 + 0.5 * r.x) * CosSin (2. * pi * r.y );
  tOff.y = r.x + r.y;
}

void SBConf (vec2 cIdBT, vec2 u)
{
  cIdB = cIdBT;
  BConf ();
  csOcc = (hgSizeB * abs (TruchSDist ((u - cMidB) / hgSizeB).x) < pWid + 0.4) ? 0. :
     0.01 + 0.99 * Hashfv2 (17.11 * cIdS);
}

float SObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, h;
  dMin = dstFar;
  if (csOcc > 0.) {
    q = p;
    q.xz = (q.xz - cMidS) / hgSizeS;
    q.xz -= tOff.xz;
    h = 0.15 + 0.15 * tOff.y;
    q.y -= h;
    d = PrCylDf (q.xzy, 0.05, h);
    DMIN (idTrnk);
    if (csOcc > 0.5) {
      q.y -= h + 0.55;
      d = PrConCapsDf (q.xzy, sin (0.085 * pi + vec2 (0.5 * pi, 0.)), 0.18, 0.4);
    } else {
      q.y -= h + 0.25;
      d = SmoothMin (PrSphDf (q, 0.25), PrSphDf (vec3 (q.x, abs (q.y) - 0.25, q.z), 0.17), 0.2);
    }
    DMIN (idLvs);
  }
  return dMin;
}

float SObjRay (vec3 ro, vec3 rd)
{
  vec3 vri, vf, hv;
  vec2 edN[3], pM, u, cIdBT;
  float dHit, d, s, eps;
  bool cNu;
  if (rd.x == 0.) rd.x = 1e-3;
  if (rd.z == 0.) rd.z = 1e-3;
  eps = 0.002;
  edN[0] = vec2 (1., 0.);
  edN[1] = 0.5 * vec2 (1., sqrt3);
  edN[2] = 0.5 * vec2 (1., - sqrt3);
  for (int k = 0; k < 3; k ++) edN[k] *= Ssign (dot (edN[k], rd.xz));
  vri = hgSizeS / vec3 (dot (rd.xz, edN[0]), dot (rd.xz, edN[1]), dot (rd.xz, edN[2]));
  vf = 0.5 * sqrt3 - vec3 (dot (ro.xz, edN[0]), dot (ro.xz, edN[1]), dot (ro.xz, edN[2])) / hgSizeS;
  dHit = 0.;
  cIdS = PixToHex (ro.xz / hgSizeS);
  pM = HexToPix (cIdS);
  cNu = true;
  for (int j = VAR_ZERO; j < 220; j ++) {
    if (cNu) {
      hv = (vf + vec3 (dot (pM, edN[0]), dot (pM, edN[1]), dot (pM, edN[2]))) * vri;
      s = Minv3 (hv);
      SConf ();
    }
    u = cMidS + tOff.xz;
    cIdBT = PixToHex (u / hgSizeB);
    if (cNu || cIdB != cIdBT) SBConf (cIdBT, u);
    cNu = false;
    d = SObjDf (ro + dHit * rd);
    if (dHit + d < s) {
      dHit += d;
    } else {
      dHit = s + eps;
      cIdS = PixToHex ((ro.xz + dHit * rd.xz) / hgSizeS);
      pM += sqrt3 * ((s == hv.x) ? edN[0] : ((s == hv.y) ? edN[1] : edN[2]));
      cNu = true;
    }
    if (d < eps || dHit > dstFar) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 SObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = 1e-3 * vec2 (1., -1.);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = SObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float SObjSShadow (vec3 ro, vec3 rd)
{
  vec3 p;
  vec2 cIdP, u;
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  cIdP = vec2 (-999.);
  for (int j = VAR_ZERO; j < 30; j ++) {
    p = ro + d * rd;
    cIdS = PixToHex (p.xz / hgSizeS);
    if (cIdS != cIdP) {
      cIdP = cIdS;
      SConf ();
    }
    u = cMidS + tOff.xz;
    SBConf (PixToHex (u / hgSizeB), u);
    if (csOcc > 0.) {
      h = SObjDf (p);
      sh = min (sh, smoothstep (0., 0.1 * d, h));
      d += h;
    } else d += 0.1 * hgSizeS;
    if (sh < 0.05 || d > 10.) break;
  }
  return 0.6 + 0.4 * sh;
}

vec4 SObjCol (vec3 ro, out vec2 vf)
{
  vec4 col4;
  if (idObj == idTrnk) {
    col4 = vec4 (0.5, 0.3, 0.1, 0.1);
    vf = vec2 (32., 1.);
  } else if (idObj == idLvs) {
    col4 = vec4 (HsvToRgb (vec3 (0.25 + 0.15 * csOcc, 0.7, 0.9)), 0.);
    col4 = mix (col4, vec4 (0.9, 0.9, 0., 0.1), step (0.7, Fbm3 (64. * ro.xzy)));
    vf = vec2 (16., 2.);
  }
  return col4;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec2 e, vf;
  vec3 col, vn, qHitF;
  float dstObj, dstObjB, dstObjS, dstObjFl, sh;
  int idObjFl, idObjB, idObjS;
  flSz = 0.12;
  pWid = 0.7;
  if (! flHide) {
    dstObjFl = FlObjRay (ro, rd);
    idObjFl = idObj;
    qHitF = qHit;
  } else dstObjFl = dstFar;
  dstObjB = BObjRay (ro, rd);
  idObjB = idObj;
  dstObjS = SObjRay (ro, rd);
  idObjS = idObj;
  dstObj = dstFar;
  if (min (min (dstObjB, dstObjS), dstObjFl) < dstFar) {
    if (dstObjFl < min (dstObjB, dstObjS)) {
      dstObj = dstObjFl;
      idObj = idObjFl;
    } else if (dstObjB < dstObjS) {
      dstObj = dstObjB;
      idObj = idObjB;
    } else {
      dstObj = dstObjS;
      idObj = idObjS;
    }
    ro += dstObj * rd;
    vf = vec2 (0.);
    if (dstObj == dstObjFl) {
      qHit = qHitF;
      vn = FlObjNf (ro);
      col4 = FlCol ();
    } else if (dstObj == dstObjB) {
      cIdB = PixToHex (ro.xz / hgSizeB);
      BConf ();
      vn = BObjNf (ro);
      col4 = BObjCol (ro, vf);
    } else if (dstObj == dstObjS) {
      vn = SObjNf (ro);
      col4 = SObjCol (ro, vf);
    }
    if (col4.a >= 0.) {
      sh = min (FlObjSShadow (ro + 0.01 * vn, sunDir), BObjSShadow (ro + 0.01 * vn, sunDir));
      sh = min (sh, SObjSShadow (ro + 0.01 * vn, sunDir));
      if (vf.y > 0.) vn = VaryNf (vf.x * ro, vn, vf.y);
      col = col4.rgb * (0.2 + 0.2 * max (dot (vn, sunDir * vec3 (-1., 1., -1.)), 0.) +
         0.8 * sh * max (dot (vn, sunDir), 0.)) +
         col4.a * step (0.95, sh) * pow (max (0., dot (sunDir, reflect (rd, vn))), 32.);
    } else col = col4.rgb * (0.6 - 0.4 * dot (rd, vn));
  } else if (rd.y < 0.) {
    ro -= (ro.y / rd.y) * rd;
    vn = vec3 (0., 1., 0.);
    col = mix (vec3 (0.4, 0.3, 0.1), vec3 (0.4, 0.5, 0.2), 2. * Fbm2 (ro.xz)) *
       (0.2 + 0.8 * max (dot (vn, sunDir), 0.));
    col = mix (col, 0.95 * vec3 (0.45, 0.55, 0.7), pow (1. + rd.y, 64.));
  } else {
    col = BgCol (ro, rd);
  }
  return clamp (col, 0., 1.);
}

vec3 ShowWg (vec2 uv, vec2 canvas, vec3 col, float parmS)
{
  vec4 wgBx[2];
  vec2 um;
  float asp, s;
  asp = canvas.x / canvas.y;
  wgBx[0] = vec4 (0.45 * asp, -0.15, 0.012 * asp, 0.2);
  wgBx[1] = vec4 (0.45 * asp, -0.45, 0.02, 0.);
  um = 0.5 * uv - wgBx[0].xy;
  s = Maxv2 (abs (um) - wgBx[0].zw);
  if (abs (s) * canvas.y < 1. || s < 0. && mod (0.5 * ((0.5 * uv.y - wgBx[0].y) / wgBx[0].w -
     0.99), 0.1) * canvas.y < 6.) col = vec3 (0.7);
  col = mix (vec3 (1.), col, smoothstep (1.5, 3.5, abs (length (um - vec2 (0., (parmS - 0.5) *
     2. * wgBx[0].w)) - 0.7 * wgBx[0].z) * canvas.y));
  col = mix (vec3 (1.), col, smoothstep (1.2, 2.2, abs (length (0.5 * uv - wgBx[1].xy) -
     wgBx[1].z) * canvas.y));
  return col;
}

#define N_WIN 3

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 stDat, mPtr;
  vec3 ro, rd, vd, col, vuPos, vuFarPos;
  vec2 canvas, uv, us, mSize, ut[N_WIN], mMid[N_WIN], msw, g;
  float zmFac, az, el, asp, sr, parmS, flPit, flDir, flRol, vuMode;
  int vuId, vuIdS, regId, wgSel;
  nFrame = iFrame;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= iResolution.x / iResolution.y;
  tCur = iTime;
  asp = canvas.x / canvas.y;
  stDat = Loadv4 (0);
  mPtr = vec4 (stDat.xyz, 0.);
  wgSel = int (stDat.w);
  stDat = Loadv4 (1);
  flPit = stDat.x;
  flDir = stDat.y;
  flRol = stDat.z;
  parmS = stDat.w;
  stDat = Loadv4 (2);
  flPos = stDat.xyz;
  stDat = Loadv4 (4);
  vuPos = stDat.xyz;
  vuMode = stDat.w;
  stDat = Loadv4 (5);
  vuFarPos = stDat.xyz;
  hgSizeB = stDat.w;
  flRol += 0.05 * smoothstep (0., 0.1, parmS) * pi * (Fbm1 (0.3 * tCur) - 0.5);
  flMat = FlMat (flPit, flDir, flRol);
  mSize = 0.2 * vec2 (asp, 1.);
  mMid[0] = (1. - mSize.y) * vec2 (asp, 1.);
  mMid[1] = (1. - mSize.y) * vec2 (- asp, 1.);
  mMid[2] = (1. - mSize.y) * vec2 (- asp, -1.);
  for (int k = 0; k < N_WIN; k ++) ut[k] = abs (uv - mMid[k]) - mSize;
  regId = -1;
  if (mPtr.z > 0.) {
    regId = 0;
    for (int k = 0; k < N_WIN; k ++) {
      msw = 2. * mPtr.xy - mMid[k] / vec2 (asp, 1.);
      if (Maxv2 (abs (msw)) < mSize.y) {
        regId = k + 1;
        msw /= 2. * mSize.y;
        break;
      }
    }
    if (regId == 0) msw = mPtr.xy;
  }
  vuId = 0;
  for (int k = 0; k < N_WIN; k ++) {
    if (Maxv2 (ut[k]) < 0.) {
      uv = (uv - mMid[k]) / mSize.y;
      vuId = k + 1;
      break;
    }
  }
  vuIdS = vuId;
  vuId = int (mod (float (vuId) + vuMode, 4.));
  az = 0.;
  el = 0.;
  if (mPtr.z > 0. && wgSel < 0 && vuIdS == regId) {
    az = 2. * pi * msw.x;
    el = 0.5 * pi * msw.y;
  }
  hgSizeS = 1.;
  dstFar = 300.;
  flHide = false;
  if (vuId == 0) {
    ro = flPos;
    az = - flDir;
    el = 0.;
    zmFac = 2.5;
    flHide = true;
  } else if (vuId == 1) {
    ro = vuPos;
    vd = normalize (flPos - ro);
    az += atan (vd.z, - vd.x) - 0.5 * pi;
    el += asin (vd.y);
    zmFac = 6.;
  } else if (vuId == 2) {
    ro = vuFarPos;
    vd = normalize (flPos - ro);
    az = atan (vd.z, - vd.x) - 0.5 * pi;
    el = asin (vd.y);
    zmFac = 50.;
  } else if (vuId == 3) {
    ro = flPos;
    ro.y += 4.;
    az = - flDir;
    el = -0.5 * pi;
    zmFac = 1.5;
  }
  vuMat = StdVuMat (el, az);
  sunDir = normalize (vec3 (-0.5, 2., -1.));
  sunDir.xz = Rot2D (sunDir.xz, 0.2 * pi * sin (0.01 * pi * tCur));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    if (vuId == 0 && parmS > 0.) rd.xy = Rot2D (rd.xy, flRol);
    rd = vuMat * rd;
    col += (1. / naa) * ShowScene (ro, rd);
  }
  for (int k = 0; k < N_WIN; k ++) {
    if (Maxv2 (ut[k]) < 0. && Minv2 (abs (ut[k])) * canvas.y < 4.)
       col = vec3 (0.8, 0.4, 0.);
  }
  if (vuIdS == 0) {
    if (vuId == 0) {
      col = mix (vec3 (1., 1., 0.5), col, 0.8 + 0.2 * smoothstep (0.002, 0.005,
         PrBoxAn2Df (Rot2D (uv, flRol), vec2 (0.6, 0.3), 0.001)));
      col = mix (vec3 (0.5, 1., 1.), col, 0.8 + 0.2 * smoothstep (0.002, 0.005,
         PrBoxAn2Df (uv, vec2 (0.3, 0.15), 0.001)));
    }
    if (vuId == 1 && mPtr.z > 0. && wgSel < 0 && Maxv2 (abs (uv)) < 0.03 &&
       Minv2 (abs (uv)) < 0.003) col = vec3 (0.8, 0.8, 0.1);
    col = ShowWg (uv, canvas, col, parmS);
    uv -= vec2 (0., -0.97) * vec2 (asp, 1.);
    if (Maxv2 (abs (uv) - vec2 (0.22, 0.05)) < 0.) {
      uv -= vec2 (0.06, -0.02);
      us = vec2 (0.1, 0.06) * vec2 (asp, 1.);
      g = floor (flPos.xz);
      col = mix (mix (col, vec3 (0., 0., 1.), 0.2), vec3 (0., 1., 1.),
         ShowInt (uv + vec2 (0.1, 0.), us, 5., g.x) + ShowInt (uv - vec2 (0.1, 0.), us, 5., g.y));
    }
  }
  fragColor = vec4 (col, 1.);
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrBoxAn2Df (vec2 p, vec2 b, float w)
{
  p = abs (p) - b;
  return max (min (max (abs (p.x), w), max (abs (p.y), w)), max (max (p.x, p.y), w)) - w;
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrRoundCylDf (vec3 p, float r, float rt, float h)
{
  return length (max (vec2 (length (p.xy) - r, abs (p.z) - h), 0.)) - rt;
}

float PrConCapsDf (vec3 p, vec2 cs, float r, float h)
{
  float d;
  d = max (dot (vec2 (length (p.xy) - r, p.z), cs), abs (p.z) - h);
  h /= cs.x * cs.x;
  r /= cs.x;
  d = min (d, min (length (vec3 (p.xy, p.z + r * cs.y - h)) - r + h * cs.y,
     length (vec3 (p.xy, p.z + r * cs.y + h)) - r - h * cs.y));
  return d;
}

float PrTorusBxDf (vec3 p, vec3 b, float ri)
{
  return length (vec2 (length (max (abs (p.xy) - b.xy, 0.)) - b.z, p.z)) - ri;
}

float Ssign (float x)
{
  return (x != 0.) ? sign (x) : 1.;
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

mat3 FlMat (float pit, float yaw, float rol)
{
  vec3 ori, ca, sa;
  ori = vec3 (pit, yaw, rol);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x) *
         mat3 (ca.z, - sa.z, 0., sa.z, ca.z, 0., 0., 0., 1.) *
         mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y , 0., ca.y);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

float HexEdgeDist (vec2 p)
{
  p = abs (p);
  return (sqrt3/2.) - p.x + 0.5 * min (p.x - sqrt3 * p.y, 0.);
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1., 0., 1.), c.y);
}

float DigSeg (vec2 q)
{
  q = 1. - smoothstep (vec2 (0.), vec2 (0.04, 0.07), abs (q) - vec2 (0.13, 0.5));
  return q.x * q.y;
}

#define DSG(q) k = kk;  kk = k / 2;  if (kk * 2 != k) d += DigSeg (q)

float ShowDig (vec2 q, int iv)
{
  vec2 vp, vm, vo;
  float d;
  int k, kk;
  vp = vec2 (0.5, 0.5);
  vm = vec2 (-0.5, 0.5);
  vo = vp - vm;
  if (iv == -1) k = 8;
  else if (iv < 2) k = (iv == 0) ? 119 : 36;
  else if (iv < 4) k = (iv == 2) ? 93 : 109;
  else if (iv < 6) k = (iv == 4) ? 46 : 107;
  else if (iv < 8) k = (iv == 6) ? 122 : 37;
  else             k = (iv == 8) ? 127 : 47;
  q = (q - 0.5) * vec2 (1.8, 2.3);
  d = 0.;
  kk = k;
  DSG (q.yx - vo);  DSG (q.xy - vp);  DSG (q.xy - vm);  DSG (q.yx);
  DSG (q.xy + vm);  DSG (q.xy + vp);  DSG (q.yx + vo);
  return d;
}

float ShowInt (vec2 q, vec2 cBox, float mxChar, float val)
{
  float nDig, idChar, s, sgn, v;
  q = vec2 (- q.x, q.y) / cBox;
  s = 0.;
  if (Minv2 (q) >= 0. && Maxv2 (q) < 1.) {
    q.x *= mxChar;
    sgn = sign (val);
    val = abs (val);
    nDig = (val > 0.) ? floor (max (log2 (val) / log2 (10.), 0.) + 0.001) + 1. : 1.;
    idChar = mxChar - 1. - floor (q.x);
    q.x = fract (q.x);
    v = val / pow (10., mxChar - idChar - 1.);
    if (idChar == mxChar - nDig - 1. && sgn < 0.) s = ShowDig (q, -1);
    if (idChar >= mxChar - nDig) s = ShowDig (q, int (mod (floor (v), 10.)));
  }
  return s;
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (mod (dot (p, vec2 (37., 39.)), 2. * pi)) * cHashM);
}

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (mod (dot (p, cHashVA2) + vec2 (0., cHashVA2.x), 2. * pi)) * cHashM);
}

vec4 Hashv4v3 (vec3 p)
{
  vec3 cHashVA3 = vec3 (37., 39., 41.);
  return fract (sin (mod (dot (p, cHashVA3) + vec4 (0., cHashVA3.xy, cHashVA3.x + cHashVA3.y),
     2. * pi)) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Noisefv3 (vec3 p)
{
  vec4 t;
  vec3 ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp *= fp * (3. - 2. * fp);
  t = mix (Hashv4v3 (ip), Hashv4v3 (ip + vec3 (0., 0., 1.)), fp.z);
  return mix (mix (t.x, t.y, fp.x), mix (t.z, t.w, fp.x), fp.y);
}

float Fbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noiseff (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm3 (vec3 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv3 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

const float txRow = 32.;

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) / txSize);
}

