  vec2 canvas, iFrag, ust;
  float tCur, tCurP, flPit, flDir, flRol, dt, asp, s, spd, tsAccum, parmS, vuMode;
  int nFrame, pxId, wgSel, wgReg;
  nFrame = iFrame;
  canvas = iResolution.xy;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / iResolution.xy - 0.5;
  iFrag = floor (fragCoord);
  pxId = int (iFrag.x + txRow * iFrag.y);
  if (pxId >= 6) discard;
  asp = canvas.x / canvas.y;
  wgReg = -2;
  if (nFrame <= 1) {
    mPtrP = mPtr;
    wgSel = -1;
    flPit = 0.;
    flDir = 0.;
    flRol = 0.;
    parmS = 0.2;
    flPos = vec3 (0., 1., 0.);
    tsAccum = 0.;
    flVel = vec3 (0.);
    vuMode = 0.;
    hgSizeB = 16.;
  } else {
    stDat = Loadv4 (0);
    mPtrP = vec4 (stDat.xyz, 0.);
    wgSel = int (stDat.w);
    stDat = Loadv4 (1);
    flPit = stDat.x;
    flDir = stDat.y;
    flRol = stDat.z;
    parmS = stDat.w;
    stDat = Loadv4 (2);
    flPos = stDat.xyz;
    tsAccum = stDat.w;
    stDat = Loadv4 (3);
    flVel = stDat.xyz;
    tCurP = stDat.w;
    stDat = Loadv4 (4);
    vuPos = stDat.xyz;
    vuMode = stDat.w;
    stDat = Loadv4 (5);
    vuFarPos = stDat.xyz;
    hgSizeB = stDat.w;
  }
  if (mPtr.z > 0.) {
    wgBx[0] = vec4 (0.45 * asp, -0.15, 0.012 * asp, 0.2);
    wgBx[1] = vec4 (0.45 * asp, -0.45, 0.02, 0.);
    ust = mPtr.xy * vec2 (asp, 1.);
    if (Maxv2 (abs (ust - wgBx[0].xy) - wgBx[0].zw) < 0.) wgReg = 0;
    if (length (ust - wgBx[1].xy) < wgBx[1].z) wgReg = 1;
    if (mPtrP.z <= 0.) wgSel = wgReg;
  } else {
    wgSel = -1;
    wgReg = -2;
  }
  if (wgSel == 0) {
    parmS = clamp (0.5 + 0.5 * (mPtr.y - wgBx[0].y) / wgBx[0].w, 0., 1.);
  } else if (wgSel == 1) {
    if (mPtrP.z <= 0.) vuMode = mod (++ vuMode, 4.);
  }
  SetPath ();
  spd = 3. * parmS;
  tsAccum += (tCur - tCurP) * spd;
  dt = max (0.3 * spd, 0.01);
  fp[0] = vec3 (EvalPPos (tsAccum), 0.).xzy;
  fp[1] = vec3 (EvalPPos (tsAccum + dt), 0.).xzy;
  fp[2] = vec3 (EvalPPos (tsAccum - dt), 0.).xzy;
  flPos.xz = fp[0].xz;
  flPos.y = 0.8;
  flVel = (fp[1] - fp[2]) / dt;
  flPit = 0.1 * spd;
  flDir = atan (flVel.z, flVel.x) - 0.5 * pi;
  va = cross ((fp[1] - 2. * fp[0] + fp[2]) / (dt * dt), normalize (flVel));
  flRol = 0.1 * length (va) * Ssign (va.y);
  flRol = smoothstep (0.01, 0.03, abs (flRol)) * flRol * min (1., 0.5 * spd);
  vuPos.xz = EvalPPos (tsAccum - 3.);
  vuPos.y = 2.;
  vuFarPos = vec3 (0., 20., (-1.5 * sqrt3 + (2. / tLen) * tsAccum) * hgSizeB);
  vuFarPos.xz = Rot2D (vuFarPos.xz, - pi / 3.);
  vuFarPos.x -= 1.5 * hgSizeB;
  if      (pxId == 0) stDat = vec4 (mPtr.xyz, float (wgSel));
  else if (pxId == 1) stDat = vec4 (flPit, flDir, flRol, parmS);
  else if (pxId == 2) stDat = vec4 (flPos, tsAccum);
  else if (pxId == 3) stDat = vec4 (flVel, tCur);
  else if (pxId == 4) stDat = vec4 (vuPos, vuMode);
  else if (pxId == 5) stDat = vec4 (vuFarPos, hgSizeB);
  Savev4 (pxId, stDat, fragColor, fragCoord);
}

