float eq(vec3 p){
    return sin(p.x*2.0) + sin(p.y*2.0) + sin(p.z*2.0);
}

//increasing helps with equations with tigher turns, but increases other artifacts
float fidelity = 1.0;
//increase for more accurate renders (but more lag)
int maxsteps = 1024;

//estimate of partial derivative
vec3 partialEstimate(vec3 p){
    vec2 e = vec2(0,0.001);
    return vec3(
        eq(p + e.yxx) - eq(p - e.yxx),
        eq(p + e.xyx) - eq(p - e.xyx),
        eq(p + e.xxy) - eq(p - e.xxy)
    )/(e.y*2.0);
}

//estimates distance to implicit function
//https://iquilezles.org/articles/distance/
float distEstimate(vec3 p){
    return abs(eq(p))/length(partialEstimate(p));
}

//returns distance to hit point
float castRay(Ray ray){
    //set march start to bounding box
    float t;
    float t2;
    boxIntersection(ray, vec3(-1), vec3(1), t, t2);
    if (t == 1e20) return 1e20; //ray does not intersect with bounding box
    ray.origin += ray.direction*t;

    //raymarch
    for (int i = 0; i < maxsteps; i++){
        float d = distEstimate(ray.origin);
        d = d/(d + fidelity); //removes some artifacts as d gets bigger
        if (d <= 1e-4) return t;
        if (t >= t2) return 1e20; //ray has exited bounding box
        if (i == 255) return t; //removes edge artifacts
        ray.origin += ray.direction*d;
        t += d;
    }
    return 1e20;
}

