#define PI 3.14159265

const float sensitivity = 4.0;
const float moveSpeed = 0.1;
const float moveDamping = 0.7;

struct Ray {
    vec3 origin;
    vec3 direction;
};

struct Camera {
    Ray ray;
    mat3 rotmat;
    vec2 res;
    vec2 uv;
};

//fakes x^n for various effects
float fpow(float x, float k){
    return x > k ? pow((x-k)/(1.0-k),2.0) : 0.0;
}

mat3 rotationMatrix(vec3 a){
    //calculate consts
    float cx = cos(a.x);
    float sx = sin(a.x);
    float cy = cos(a.y);
    float sy = sin(a.y);
    float cz = cos(a.z);
    float sz = sin(a.z);
    //return matrix
    return mat3(
        cy*cz, sx*sy*cz - cx*sz, cx*sy*cz + sx*sz,
        cy*sz, sx*sy*sz + cx*cz, cx*sy*sz - sx*cz,
        -sy, sx*cy, cx*cy
    );
}

// https://iquilezles.org/articles/intersectors/
// axis aligned box centered at the origin, with size boxSize
void boxIntersection( Ray ray, vec3 aaa, vec3 bbb, out float t0, out float t1){
    vec3 boxSize = abs(bbb - aaa);
    vec3 m = 1.0/ray.direction; // can precompute if traversing a set of aligned boxes
    vec3 n = m*ray.origin;   // can precompute if traversing a set of aligned boxes
    vec3 k = abs(m)*boxSize;
    vec3 h1 = -n - k;
    vec3 h2 = -n + k;
    float tN = max(max( h1.x, h1.y ), h1.z);
    float tF = min(min( h2.x, h2.y ), h2.z);
    if (tN > tF) { // no intersection
        t0 = 1e20;
        t1 = 1e20;
        return;
    }
    t0 = min(tN, tF);
    t1 = max(tN, tF);
    return;
}

vec3 hsvToRgb(vec3 hsv){ //hsv.x = hue, hsv.y = saturation, hsv.z = value
    vec3 col = vec3(hsv.x, hsv.x + 2.0/3.0, hsv.x + 4.0/3.0); //inputs for r, g, and b
    col = clamp(abs(mod(col*2.0, 2.0)-1.0)*3.0 - 1.0, 0.0, 1.0)*hsv.z*hsv.y + hsv.z - hsv.z*hsv.y; //hue function (graph it on desmos)
    return col;
}

//returns a point rotated in 3d (radians)
vec3 rotate3d(vec3 point, vec3 angle, vec3 origin){ 
    point -= origin; //subtract pivot
    point *= rotationMatrix(angle); //rotate point
    point += origin; //add pivot
    return point;
}

//camera constructor
Camera createCamera(vec2 fragCoord, vec2 resolution, vec3 camRot, float screensize){
    vec3 uv = vec3((fragCoord - resolution*0.5)/resolution.y, 0)*screensize;
    mat3 rotmat = rotationMatrix(camRot);
    vec3 rd = vec3(0,0,1)*rotmat;
    vec3 ro = uv*rotmat - rd*100.0;
    return Camera(Ray(ro, -rd), rotmat, resolution, uv.xy);
}

