mat2 rot(float a) {
  float ca=cos(a);
  float sa=sin(a);
  return mat2(ca,sa,-sa,ca);
}



float cylcap( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}


float hash11(float t) {
  return fract( sin(t * 7361.994) * 4518.442);
}

vec3 lattice (float t ) {



  float m = t;
  float mx = floor((m-2.)/3.);
  float my = floor((m-1.)/3.);
  float mz = floor((m-0.)/3.);

  float n= m+1.;
  float nx = floor((n-2.)/3.);
  float ny = floor((n-1.)/3.);
  float nz = floor((n-0.)/3.);


  vec3 a =  
  mix(
      vec3(
          hash11(mx)-.5,
          hash11(my)-.5,
          hash11(mz)-.5
      ),
      vec3(
          hash11(nx)-.5,
          hash11(ny)-.5,
          hash11(nz)-.5
      ) , 
      fract(t)
  );
  return a;
}



float hash31(vec3 p3)
{
	p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}


float closestWall(vec3 p) {
    vec3 d = min(fract(p), 1.0 - fract(p));
    return min(min(d.x, d.y), d.z);
}


float map (vec3 p){


   // geo
   float tt = iTime;

   vec3 id = floor(p)-.5; 
   p = fract(p) -.5;
   float f1 = hash31(id);

   p.yx *= rot(f1 * 11.1 * ( sin(iTime)*.5+.5));
   p.yz *= rot(f1 * 9.1 * ( sin(iTime)*.5+.5));

   float ball = cylcap(p, .03,.3);
   float bound = closestWall(p);

   return min(bound + .03, ball);
}

vec3 norm(vec3 p) {
  vec2 off=vec2(0.001,0.0);
  return normalize(map(p)-vec3(map(p-off.xyy), map(p-off.yxy), map(p-off.yyx))+0.000001);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);

  float tt = iTime * 2.;

  // nav

  vec3 s,t;

  s = lattice(tt*.1) * 18.;
  t = lattice(tt *.1 + 2.) * 18.;


  vec3 cz=normalize(t-s);
  vec3 cx=normalize(cross(cz,vec3(0,1,0)));
  vec3 cy=-normalize(cross(cz,cx));
  vec3 r=normalize(cx*uv.x+cy*uv.y+cz);


  vec3 p=s;
  float i=0.;
  float dd = 0.;
  float d;

  // march

  bool hit = false;
  for(float z=0.; z < 500.; ++z) {
    i = z;
