// Voronoi formula by iq - https://www.shadertoy.com/view/MslGD8

#define TAU 6.28318530718

vec2 hash( vec2 p )
{
    p = vec2(dot(p,vec2(127.1,311.7)),
             dot(p,vec2(269.5,183.3)));
    return fract(sin(p)*18.5453);
}

vec2 voronoi(in vec2 x)
{
    vec2 n = floor(x);
    vec2 f = fract(x);

    vec3 m = vec3(8.,8.,8.);
    for(int j=-1; j<=1; j++)
        for(int i=-1; i<=1; i++)
        {
            vec2 g = vec2(float(i), float(j));
            vec2 o = hash(n + g);
            vec2 r = g - f + o;
            float d = dot(r, r);
            if(d<m.x)
                m = vec3(d, o);
        }

    return vec2(sqrt(m.x), m.y+m.z );
}

vec2 toPolar(vec2 p)
{
    return vec2(atan(p.y, p.x)/TAU,length(p));
}

