  p *= mix(1.0, 0.5, h0*h0);
  float a = 0.5*h2*(h1-0.5)*ptime;
  p *= ROT(0.25*(h0-0.5)*ptime);
  p -= vec2(h0, h1)-0.5;
  if (h2 <  0.5) {
    p =abs(p);
  }
  p *= ROT(a+sqrt(0.5)*sin(a)+h0*TAU);
  vec3 col = effect(p);

  col = 0.8*smoothstep(vec3(0.0), vec3(1.25, 1.5, 1.0), col);
//  col = clamp(col, 0.0, 1.0);
  col *= col;

  fragColor = vec4(col, 1.0);
}

