////////////////////////////////////////////////////////////////
// Tristan Antonsen 2023
////////////////////////////////////////////////////////////////
// References
// Distance functions & structure based on Inigo Quilez's (iq) shaders & functions https://iquilezles.org/
// Ray marcher inspired by iq's work and The Art of Code's work (https://www.shadertoy.com/view/XlGBW3)

// Ray marching constants
#define MAX_STEPS 350
#define SURF_DIST 0.0001
#define MAX_DIST 100.0
#define PI 3.141592653592
#define TAU 6.283185307185

////////////////////////////////////////////////////////////////
// Noise
////////////////////////////////////////////////////////////////

// Hash & voronoi from iq: https://www.shadertoy.com/view/ldl3Dl
vec3 hash( vec3 x )
{
	x = vec3( dot(x,vec3(127.1,311.7, 74.7)),
			  dot(x,vec3(269.5,183.3,246.1)),
			  dot(x,vec3(113.5,271.9,124.6)));

	return fract(sin(x)*43758.5453123);
}
vec3 voronoi( in vec3 x )
{
    vec3 p = floor( x );
    vec3 f = fract( x );

	float id = 0.0;
    vec2 res = vec2( 100.0 );
    for( int k=-1; k<=1; k++ )
    for( int j=-1; j<=1; j++ )
    for( int i=-1; i<=1; i++ )
    {
        vec3 b = vec3( float(i), float(j), float(k) );
        vec3 r = vec3( b ) - f + hash( p + b );
        float d = dot( r, r );

        if( d < res.x )
        {
			id = dot( p+b, vec3(1.0,57.0,113.0 ) );
            res = vec2( d, res.x );			
        }
        else if( d < res.y )
        {
            res.y = d;
        }
    }

    return vec3( sqrt( res ), abs(id) );
}

////////////////////////////////////////////////////////////////
// Signed Distance Functions
////////////////////////////////////////////////////////////////
// Main reference: https://iquilezles.org/articles/distfunctions/

float sdPlane( vec3 p, vec3 n, float h )
{
  return dot(p,normalize(n)) + h;
}

float sdSphere( vec3 p, vec3 c, float r )
{
    return length(p-c) - r;
}

float sdEllipsoid( vec3 po, vec3 c, vec3 r )
{
    vec3 p = po-c;

    float k0 = length(p/r);
    float k1 = length(p/(r*r));
    return k0*(k0-1.0)/k1;
}

float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
    vec3 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h ) - r;
}

float sdRoundedBox(vec3 p, vec3 s, float r)
{
    // Modified to account for the radius without changing the size of the box
    p = abs(p)-(s-r);
    return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.) - r;
}

float sdRoundedCylinder( vec3 p, float ra, float rb, float h )
{
    vec2 d = vec2( length(p.xz)-2.0*ra+rb, abs(p.y) - h );
    return min(max(d.x,d.y),0.0) + length(max(d,0.0)) - rb;
}

float sdCutSphere( vec3 p, float r, float h )
{
  // sampling independent computations (only depend on shape)
  float w = sqrt(r*r-h*h);

  // sampling dependant computations
  vec2 q = vec2( length(p.xz), p.y );
  float s = max( (h-r)*q.x*q.x+w*w*(h+r-2.0*q.y), h*q.x-w*q.y );
  return (s<0.0) ? length(q)-r :
         (q.x<w) ? h - q.y     :
                   length(q-vec2(w,h));
}


////////////////////////////////////////////////////////////////
// SDF Operations
////////////////////////////////////////////////////////////////

float opUnion(float d1, float d2 ) { return min(d1,d2); }

float opSubtraction(float d1, float d2) {
    //NOTE: Flipped order because it makes more sense to me
    return max(-d2, d1);
}
float opIntersection(float d1, float d2) {
    return max(d1, d2);
}

float opSmoothUnion(float d1, float d2, float k) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}
float opSmoothSubtraction(float d1, float d2, float k) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d1, -d2, h ) + k*h*(1.0-h);
}
float opSmoothIntersection(float d1, float d2, float k){
    float h = clamp( 0.5 - 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) + k*h*(1.0-h);
}

////////////////////////////////////////////////////////////////
// Bezier stuff
////////////////////////////////////////////////////////////////


vec3 bezier(vec3 p1, vec3 p2, vec3 p3, float t) {

    vec3 p12 = mix(p1, p2, t);
    vec3 p23 = mix(p2, p3, t);
    vec3 p123 = mix(p12, p23, t);


    return p123;
}

float sdBezier3D(vec3 p, vec3 p1, vec3 p2, vec3 p3, float rad, float dt) {

    float bez = 1e6;
    for (float i = dt; i < 1.+dt; i += dt) {

        vec3 b0 = bezier(p1, p2, p3, max(i-dt, 0.));
        vec3 b1 = bezier(p1, p2, p3, min(i, 1.));        
        float seg = sdCapsule(p, b0, b1, rad);

        bez = opUnion(bez, seg);
    }

    return bez;
}




////////////////////////////////////////////////////////////////
// Rotations
////////////////////////////////////////////////////////////////

vec3 rotX(vec3 p, float a) {
    float s = sin(a);
    float c = cos(a);
    mat3 m = mat3(
        1., 0., 0.,
        0., c, -s,
        0., s, c
        );
    return m * p;
}

vec3 rotY(vec3 p, float a) {
    float s = sin(a);
    float c = cos(a);
    mat3 m = mat3(
        c, 0., s,
        0., 1., 0.,
        -s, 0., c
        );
    return m * p;
}

vec3 rotZ(vec3 p, float a) {
    float s = sin(a);
    float c = cos(a);
    mat3 m = mat3(
        c, -s, 0.,
        s, c, 0.,
        0., 0., 1.
        );
    return m * p;
}

vec3 orbitControls(vec3 po) {
    vec2 m = (vec2(iMouse.x, iMouse.y) / iResolution.xy) + 0.5;
    vec3 p = po;
    p = rotY(po, -m.x*TAU - PI);
    //p = rotX(p, -m.y*PI - PI / 2.0);
    return p;
}

////////////////////////////////////////////////////////////////
// Ray Marching Functions
////////////////////////////////////////////////////////////////

vec2 map(vec3 po) {
    // Mouse
    vec2 m = (2.*iMouse.xy-iResolution.xy)/iResolution.y;  
    vec3 p = rotY(po, iTime);

    vec2 res;
    vec3 pSym = vec3(abs(p.x), p.yz);

    vec3 p1 = vec3(-0.75, 0., 0.25*cos(iTime));
    vec3 p2 = vec3(0., m.y, 0.);
    vec3 p3 = vec3(0.75, 0., 0.25*sin(iTime));


    res.y = 1.0;
    res.x = sdSphere(p, p1,0.1);
    res.x = opUnion(res.x, sdSphere(p, p2,0.1));
    res.x = opUnion(res.x, sdSphere(p, p3,0.1));


    float dt = 0.05;
    vec3 b0;
    vec3 b1;
    float bez = sdBezier3D(p, p1, p2, p3, 0.02, dt);

    if (bez < res.x) { res.y = 2.0; };

    res.x = opUnion(res.x, bez);

    return res;
}

vec3 rayMarch(vec3 ro, vec3 rd) {
    float d = 0.0;
    float mat = 1.0;
    float minDist = MAX_DIST;
    for (int i = 0; i < MAX_STEPS; i++) {
        if (i >= MAX_STEPS ) break;
        vec3 p = ro + rd * d;
        vec2 ds = map(p);
        d += ds.x;
        mat = ds.y;
        minDist = min(minDist, ds.x);
        if (d >= MAX_DIST || ds.x < SURF_DIST) break;
        i++;
    }
    return vec3(d, mat, minDist);
}

vec3 gradient(vec3 p) {
    float epsilon = 0.0001;
    vec3 dx = vec3(epsilon, 0., 0.0);
    vec3 dy = vec3(0., epsilon, 0.0);
    vec3 dz = vec3(0., 0.0, epsilon);

    float ddx = map(p + dx).x - map(p - dx).x;
    float ddy = map(p + dy).x - map(p - dy).x;
    float ddz = map(p + dz).x - map(p - dz).x;

    return normalize(vec3(ddx, ddy, ddz));
}

vec3 rayDirection(vec2 p, vec3 ro, vec3 rt) {

    // screen orientation
    vec3 vup = vec3(0., 1.0, 0.0);
    float aspectRatio = iResolution.x / iResolution.y;

    vec3 vw = normalize(ro - rt);
    vec3 vu = normalize(cross(vup, vw));
    vec3 vv = cross(vw, vu);
    float theta = radians(30.); // half FOV
    float viewport_height = 2. * tan(theta);
    float viewport_width = aspectRatio * viewport_height;
    vec3 horizontal = -viewport_width * vu;
    vec3 vertical = viewport_height * vv;
    float focus_dist = length(ro - rt);
    vec3 center = ro - vw * focus_dist;

    vec3 rd = center + p.x * horizontal + p.y * vertical - ro;

    return normalize(rd);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord / iResolution.xy - 0.5) * 2.0; // normalizing

    // Ray Marching
    vec3 rt = vec3(0., 0., 0.);
    vec3 ro = vec3(0., 0., -10.02);

    vec3 rd = rayDirection(uv, ro, rt);
    vec3 d = rayMarch(ro, rd);

    // Background
    float v = length(uv) * .75;
    fragColor = vec4(0.1,0.7,1.,1.);
    fragColor += vec4(mix(0.2, 0.4, smoothstep(0.0, 1.0, uv.y)));
    fragColor += vec4(mix(0.2, 0.4, smoothstep(0.0, 1.0, uv.x)));

    vec3 color;

    if (d.x <= 100.0) {
        vec3 p = ro + rd * d.x;
        vec3 pr = orbitControls(p);
        vec3 N = gradient(p);
        float light1 = dot(N, normalize(vec3(1, 1,-1)))*.5+.5;
        float light2 = dot(N, normalize(vec3(-1, 1,-1)))*.5+.5;
        vec3 illumination = 0.5 * vec3(light1) + 0.5 * vec3(light2);

        if (d.y == 1.0) color = vec3(0.2,0.8,1.);
        if (d.y == 2.0) color = vec3(0.9, 0.1, 0.1);
        if (d.y == 3.0) color = vec3(0.8,0.,0.);
        color *= illumination; 


        // Specular highlights
        vec3 R = reflect(normalize(vec3(1.,0.,0.)), N);
        vec3 specular = vec3(1.0) * pow(max(dot(R, rd), 0.0),10.0);
        color += specular * 0.08;


        // fake fresnel
        float nDotV = dot(N, rd) + 1.;
        color += nDotV * nDotV * 0.45;

        fragColor = vec4(color, 1.0);


    }



}

