vec3 rotX(vec3 p, float a) {
    float s = sin(a);
    float c = cos(a);
    mat3 m = mat3(
        1., 0., 0.,
        0., c, -s,
        0., s, c
        );
    return m * p;
}

vec3 rotY(vec3 p, float a) {
    float s = sin(a);
    float c = cos(a);
    mat3 m = mat3(
        c, 0., s,
        0., 1., 0.,
        -s, 0., c
        );
    return m * p;
}

vec3 rotZ(vec3 p, float a) {
    float s = sin(a);
    float c = cos(a);
    mat3 m = mat3(
        c, -s, 0.,
        s, c, 0.,
        0., 0., 1.
        );
    return m * p;
}

vec3 orbitControls(vec3 po) {
