////////////////////////////////////////////////////////////////
// Ray Marching Functions
////////////////////////////////////////////////////////////////

float body1(vec3 p) {
    float f = 0.15;
    float r = 0.1;
    float R = 0.375;

    float d = sdSphere(p, vec3(0.45 * sin(f * iTime), 0.5 * cos(f * iTime + PI / 2.), 0.), R);
    return d;
}

float body2(vec3 p) {
    float f = 0.15;
    float R = 0.375;

    vec3 center = p - vec3(0.45 * cos(f * iTime), 0.5 * sin(f * iTime), 0.);
    center = rotY(center, 0.25 * iTime);
    center = rotX(center, 0.25 * iTime);

    float d = sdRoundedBox(center, vec3(0.3), 0.05);
    return d;
}

float body3(vec3 p) {
    float f = 0.15;
    float R = 0.375;

    vec3 center = p - vec3(0.5 * cos(f * iTime));
    center = rotZ(center, 0.25 * iTime);
    float d = sdRoundedCylinder(center, 0.125, 0.1, 0.5);

    return d;
}

vec2 map(vec3 p) {
    vec2 res;
    vec3 pSym = vec3(abs(p.x), p.yz);

    float r = 0.1;

    float s1 = body1(p);
    res = vec2(s1, 0.0); // id to track color

    float s2 = body2(p);
    if (s2 < res.x) { res.y = 1.0; };
    res.x = opSmoothUnion(res.x, s2, r);

    float s3 = body3(p);
    if (s3 < res.x) { res.y = 2.0; };

    res.x = opSmoothUnion(res.x, s3, r);

    return res;
}

vec3 rayMarch(vec3 ro, vec3 rd) {
    float d = 0.0;
    float mat = 1.0;
    float minDist = MAX_DIST;
    for (int i = 0; i < MAX_STEPS; i++) {
        if (i >= MAX_STEPS ) break;
        vec3 p = ro + rd * d;
        vec2 ds = map(p);
        d += ds.x;
        mat = ds.y;
        minDist = min(minDist, ds.x);
        if (d >= MAX_DIST || ds.x < SURF_DIST) break;
        i++;
    }
    return vec3(d, mat, minDist);
}

vec3 gradient(vec3 p) {
    float epsilon = 0.0001;
    vec3 dx = vec3(epsilon, 0., 0.0);
    vec3 dy = vec3(0., epsilon, 0.0);
    vec3 dz = vec3(0., 0.0, epsilon);

    float ddx = map(p + dx).x - map(p - dx).x;
    float ddy = map(p + dy).x - map(p - dy).x;
    float ddz = map(p + dz).x - map(p - dz).x;

    return normalize(vec3(ddx, ddy, ddz));
}

vec3 rayDirection(vec2 p, vec3 ro, vec3 rt) {

    // screen orientation
    vec3 vup = vec3(0., 1.0, 0.0);
    float aspectRatio = iResolution.x / iResolution.y;

    vec3 vw = normalize(ro - rt);
    vec3 vu = normalize(cross(vup, vw));
    vec3 vv = cross(vw, vu);
    float theta = radians(30.); // half FOV
    float viewport_height = 2. * tan(theta);
    float viewport_width = aspectRatio * viewport_height;
    vec3 horizontal = -viewport_width * vu;
    vec3 vertical = viewport_height * vv;
    float focus_dist = length(ro - rt);
    vec3 center = ro - vw * focus_dist;

    vec3 rd = center + p.x * horizontal + p.y * vertical - ro;

    return normalize(rd);
}

vec3 tripleGradient(float fac, float blend) {

    vec3 c1 = vec3(1., 0., 0.);
    vec3 c2 = vec3(0., 1., 0.);
    vec3 c3 = vec3(0., 0., 1.);



    vec3 ca = mix(c1, c2, smoothstep(0.5 - blend, 0.5, fac));
    vec3 cb = mix(c2, c3, smoothstep(0.5, 0.5 + blend, fac));
    vec3 c = mix(ca, cb, smoothstep(0.5 - blend, 0.5 + blend, fac));

    return c;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Mouse
    vec2 m = (2.*iMouse.xy-iResolution.xy)/iResolution.y;  

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord / iResolution.xy - 0.5) * 2.0; // normalizing

    // Ray Marching
    vec3 rt = vec3(0., 0., 0.);
    vec3 ro = vec3(0., 0., -10.02);

    vec3 rd = rayDirection(uv, ro, rt);
    vec3 d = rayMarch(ro, rd);

    // Casting a single ray
    vec3 rdm = rayDirection(m, ro, rt);
    vec3 dm = rayMarch(ro, rdm);


    // Background
    float v = length(uv) * .75;
    fragColor = vec4(0.1,0.7,1.,1.);
    fragColor += vec4(mix(0.2, 0.4, smoothstep(0.0, 1.0, uv.y)));
    fragColor += vec4(mix(0.2, 0.4, smoothstep(0.0, 1.0, uv.x)));

    vec3 color;

    if (d.x <= 100.0) {
        vec3 p = ro + rd * d.x;
        vec3 pr = orbitControls(p);
        vec3 N = gradient(p);
        float light1 = dot(N, normalize(vec3(1, 1,-1)))*.5+.5;
        float light2 = dot(N, normalize(vec3(-1, 1,-1)))*.5+.5;
        vec3 illumination = 0.5 * vec3(light1) + 0.5 * vec3(light2);

        // re-calculation, might be a way to avoid this
        float s1 = body1(p);
        float s2 = body2(p);
        float s3 = body3(p);
        float sdfs[3] = float[3](s1, s2, s3);    
        //

        color = vec3(0.,0.8,1.);

        float highlighted = sdfs[int(dm.y)];

        vec3 selectionColor = vec3(1., 0.9, 0.);


        if (iMouse.z > 0.0 && dm.x < 100.) {
            color = mix(selectionColor, color, smoothstep(0., 0.1, highlighted));
        };

        color *= illumination; 


        // Specular highlights
        vec3 R = reflect(normalize(vec3(1.,0.,0.)), N);
        vec3 specular = vec3(1.0) * pow(max(dot(R, rd), 0.0),10.0);
        color += specular * 0.08;


        // fake fresnel
        float nDotV = dot(N, rd) + 1.;
        color += nDotV * nDotV * 0.45;

        fragColor = vec4(color, 1.0);


    }



}

