mat2 rot(float deg){
	return mat2(cos(deg),sin(deg),-sin(deg),cos(deg));
}

float beat_time(float t){
	float a = 2.*pi / spb;
	return t + sin(a*t)/a;
}

vec2 cubic_bezier(vec2 p0, vec2 p1, vec2 p2, vec2 p3, float t){
	return mix(mix(mix(p0,p1,t),mix(p1,p2,t),t),mix(mix(p1,p2,t),mix(p2,p3,t),t),t);
}

vec2 cubic_bezier_diff(vec2 p0, vec2 p1, vec2 p2, vec2 p3, float t){
	return mix(mix(p1,p2,t),mix(p2,p3,t),t)-mix(mix(p0,p1,t),mix(p1,p2,t),t);
}



vec2 parametric(float t, float curve){
	if(curve == 4.){
		if(t < .5){
			return cubic_bezier(p0,p1,p2,p3,2.*t);
		}
		return cubic_bezier(q0,q1,q2,q3,2.*t-1.);
	}

	t *= 2.*pi;

	if(curve == 0.){
		return rad*vec2(sin(t),cos(t));
	}
	if(curve == 1.){
		t*=.5;
		return rad*cos(3.*t)*vec2(sin(t),cos(t));
	}
	if(curve == 2.){
		return .4*rad*vec2(2.*cos(t)+cos(2.*t),-2.*sin(t)+sin(2.*t)).yx-vec2(0.,.12);
	}
	if(curve == 3.){
		return 1.2*rad*t*vec2(sin(3.*t),cos(3.*t))/2./pi;
	}
	if(curve == 5.){
		vec2 val=vec2(0);
		for(int i=1;i<4;i++){
			val += random_curve[i-1].xz * cos(float(i)*t) / float(i);
			val += random_curve[i-1].yw * sin(float(i)*t) / float(i);
		}

		return val;
	}
}

vec2 parametric_diff(float t, float curve){
	if(curve == 4.){
		if(t < .5){
			return 2.*cubic_bezier_diff(p0,p1,p2,p3,2.*t);
		}
		return 2.*cubic_bezier_diff(q0,q1,q2,q3,2.*t-1.);
	}

	t *= 2.*pi;

	if(curve == 0.){
		return 2.*pi*rad*vec2(cos(t),-sin(t));
	}
	if(curve == 1.){
		t*=.5;
		return rad*2.*pi*vec2(cos(3.*t)*cos(t) - 3.*sin(3.*t)*sin(t),-3.*cos(t)*sin(3.*t) - cos(3.*t)*sin(t));
	}
	if(curve == 2.){
		if(abs(t) < 1./big){
			return vec2(0,1);
		}
		return .4*rad*4.*pi*vec2(-sin(t)-sin(2.*t),-cos(t)+cos(2.*t)).yx;
	}
	if(curve == 3.){
		return 1.2*rad*vec2(3.*t*cos(3.*t) + sin(3.*t),-3.*t*sin(3.*t) + cos(3.*t));
	}
	if(curve == 5.){
		vec2 val = vec2(0);
		for(int i=1;i<4;i++){
			val += float(i) * random_curve[i-1].yw * cos(float(i)*t) / float(i);
			val -= float(i) * random_curve[i-1].xz * sin(float(i)*t) / float(i);
		}
		return pi*val;
	}
}

vec3 get_normal(float t, float curve){
	vec2 p0 = parametric(t,curve);
	vec2 tang = normalize(parametric_diff(t,curve));

	return vec3(tang,-dot(tang,p0));
}

vec3 get_tangent(float t, float curve){
	vec2 p0 = parametric(t,curve);
	vec2 tang = parametric_diff(t,curve);
	vec2 nor = normalize(vec2(tang.y,-tang.x));

	return vec3(nor,-dot(nor,p0));
}

