const float TWO_PI = 6.28318530718;
const vec2 LISTENER_POS_L = vec2(1.0, 0.0);
const vec2 LISTENER_POS_R = LISTENER_POS_L;
const float SPREAD = 0.4;
const float FREQ = 64.0;
const float MOVE_AMOUNT = 1.0;
const float MOVE_SPEED = 4.0;
const int EMITTER_COUNT = int(FREQ * 4.0 * SPREAD); // Set to atleast (FREQ * 4 * SPREAD) to avoid multiple transmitter lines

float wave(vec2 uv, vec2 p, float o, float f, float t)
{
    float d = distance(uv, p);
    float w = sin(((d - t) * f - o) * TWO_PI) / (1.0 + 256.0 * d * d);
    return w;
}

float getWave(vec2 uv, float t)
{
    float w = 0.0;
    // Higher frequency = Further travel (Until it glitches out and everything explodes, don't try at home)
    // You can change direction by offsetting either frequency or phase (offsetting frequency has side effects, so usually phase is offset)
    for (float i = 0.0; i <= SPREAD; i += SPREAD / float(EMITTER_COUNT - 1))
        w += wave(uv, vec2(0, i * 2.0 - SPREAD), i * sin(t * MOVE_SPEED) * MOVE_AMOUNT * FREQ, FREQ, t);
    w /= float(EMITTER_COUNT) * 0.04;

    return w;
}

