    vec2 uv = (2.*fragCoord-iResolution.xy) / max(iResolution.x, iResolution.y); //long edge -1 to 1, square aspect ratio
    float amp = 0.;
	float delY = .1;
	float r, ox, fun;
    compressFft(); //initializes fft, ffts


    // Lane generation
	for (float m=-1.;m<=1.;m+=.05){
        ox = m;
        fun = uv.x-ox;
        for (float n=0.;n<2./delY;n++){ //uv.y -1..1
            r = floor(hash21(vec2(n+ceil(aTime/16.),ox+max(.98*step(fft.x,.96),fract(aTime/16.))))*3.-1.); //r {-1,0,1}
            fun += (r*(uv.y-(delY*n-1.))+ox+m) * step(delY*n-1.,uv.y+.001) * step(uv.y+.001,delY*(n+1.)-1.); //+.001 to avoid weird? singularities for negative uv.y
            ox = r*(delY*(n+1.)-1.) - (r*(delY*n-1.)-ox-m) - m; //? isnt that supposed to be: ox += r*delY ?
        } 
		amp += (.8+.5*ffts[int(m*m*3.)]) * ( //brightness on voice
            aaStep(abs(fun)-.009*iResolution.y/iResolution.x) //lane
            * (.1+.9*aaStep(abs(fragCoord.y/iResolution.y-fract(aTime/8.+m*(.6+.4*ffts.w)))-.005*(1.+17.*step(.95,fft.x))*iResolution.y/iResolution.x)) //step "spark"
            + 1e-3 / length(vec2(fun, fragCoord.y/iResolution.y-fract(aTime/8.+m*(.6+.4*ffts.w))))); //glow "spark"
	} 


    // Color design
    vec3 col = vec3(clamp(amp,.0,1.)); 
    col += col * max(.0,1.-5.*fragCoord.y/iResolution.y*(1.-fft.x*fft.x*fft.x)) //bass level dependend brightness, y-axis
        + smoothstep(.0,1.,.2-amp) * fft.w; //lighten darker background
    col *= abs( cos( .06*aTime + PI/vec3(.5,2.,4.) + ffts.xyz)); //color shift 
    col *= mix( .2+.8*(1.-length(uv)), 1., fft.z); //vignette


    // Center symbol
    uv *= .8+.5*(1.-fft.x*fft.x); 
    fun = mix(
        abs(length(uv)-.23)-.07, //circle
        (sin(aTime/4.+fft.y-.5)<.0)? //switch when unused
            abs(dot(abs(uv*rotM(PI/4.)),vec2(1))-.25)-.1 : //square
            abs(sdEquilateralTriangle(uv*4.))-.27, //triangle
        clamp( sin(aTime/2.+fft.y-.5), .0, 1.));

    col = vec3(ffts.x<=ffts.y,ffts.y<=ffts.z,ffts.z<=ffts.x) * fft.z * .08 //overall tint 
        + abs(1.-col) * aaStep(fun) * fft.y * .13 //symbol
        + col * (1.-step(length(col),.9)*aaStep(fun)) * .8; //background

    fragColor = vec4(col,1);
}

vec3 palette(float t){
    return vec3(0.5 + 0.5 * sin(t * 5.0), 0.2 + 0.2 * sin(t * 3.0), 0.1 + 0.4 * sin(t));
}

float map(vec3 p, float time){
    float d = length(p) - 1.0;
    float displacement = 0.1 * sin(time + 3.0 * p.x) * sin(time + 3.0 * p.y);
    return d + displacement;
}

vec4 rm(vec3 ro, vec3 rd, float time){
    float t = 0.0;
    vec3 col = vec3(0.0);
    float d;
    for(float i = 0.0; i < 64.0; i++){
        vec3 p = ro + rd * t;
        d = map(p, time) * 0.5;
        if(d < 0.02){
            break;
        }
        if(d > 100.0){
            break;
        }
        col += palette(time + length(p) * 0.1) / (400.0 * (d));
        t += d;
    }
    return vec4(col, 1.0 / (d * 100.0));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = (fragCoord - (iResolution.xy / 2.0)) / iResolution.x;
    vec3 ro = vec3(0.0, 0.0, -15.0);
    vec3 cf = normalize(vec3(0.0, 0.0, 1.0));
    vec3 cs = normalize(cross(cf, vec3(0.0, 1.0, 0.0)));
    vec3 cu = normalize(cross(cf, cs));

    vec3 uuv = ro + cf * 3.0 + uv.x * cs + uv.y * cu;

    vec3 rd = normalize(uuv - ro);

    vec4 col = rm(ro, rd, iTime);

    fragColor = col;
}

vec4 iridescent(in vec3 normal, in vec2 uv, in float alpha) {
    // Convert to polar coordinates
    float r = length(uv);
    float theta = atan(uv.y * iTime, uv.x * iTime);

    float factor = 20.0;

    // Apply a twist
    theta += factor / 4.0 * normal.x * r;

    // Convert back to Cartesian coordinates
    vec2 twistedUV = r * vec2(cos(theta), sin(theta));


    // Swirly pattern based on twisted UV
    vec3 swirl = vec3(
        sin(factor * twistedUV.x + 3.0 * sin(1.23 * iTime) ),
        sin(factor * twistedUV.y + 3.0 * sin(1.59 * iTime)),
        sin(factor * twistedUV.x + factor * twistedUV.y + 3.0 * sin(1.17 * iTime))
    );

    // Base iridescent color
    float thickness = 500.0 + 100.0 * sin(iTime);
    float R = abs(thickness - 400.0) / 400.0;
    float G = abs(thickness - 500.0) / 500.0;
    float B = abs(thickness - 600.0) / 600.0;
    vec3 baseColor = vec3(R, G, B);

    // Blend swirly pattern with base color
    vec3 color = mix(baseColor, swirl, 0.8);

    return vec4(color, alpha);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    // Camera parameters
    vec3 camPos = vec3(0.0, 0.0, 5.0);
    vec3 camTarget = vec3(0.0, 0.0, 0.0);
    vec3 camDir = normalize(camTarget - camPos);
    vec3 camUp = vec3(0.0, 1.0, 0.0);
    vec3 camRight = cross(camDir, camUp);

    // Construct a ray from the camera through the pixel
    vec3 rayDir = normalize(camDir + uv.x * camRight + uv.y * camUp);

    // Bubble parameters
    vec3 bubbleCenter = vec3(sin(1.7 * iTime), sin(1.13 * iTime), sin(1.11 * iTime));
    float radius = 0.55;

    // Calculate the ray-sphere intersection
    vec3 oc = camPos - bubbleCenter;
    float a = dot(rayDir, rayDir);
    float b = 2.0 * dot(oc, rayDir);
    float c = dot(oc, oc) - (radius * radius);
    float discriminant = b*b - 4.0*a*c;

    vec4 bgColor = texture(iChannel0, uv);

    if (discriminant > 0.0) {
        // If the ray intersects the sphere, calculate the distance along the ray to the intersection
        float t1 = (-b - sqrt(discriminant)) / (2.0 * a);
        float t2 = (-b + sqrt(discriminant)) / (2.0 * a);
        float t = min(t1, t2);

        // Calculate the intersection point
        vec3 intersection = camPos + t * rayDir;

        // Compute the normal at the intersection
        vec3 normal = normalize(intersection - bubbleCenter);

        // Compute the distance from the camera to the intersection
        float dist = length(intersection - camPos);

        // Use the distance and normal to compute the iridescent color
        fragColor = mix(iridescent(normal, uv, 0.2),bgColor,0.8);
    } else {
        fragColor = bgColor;
    }
}

