// from https://www.shadertoy.com/view/lsKSWR by Ippokratis
float vignette(vec2 FC, float extent, float intensity) {
	vec2 uv = FC.xy / iResolution.xy;
    uv *=  1.0 - uv.yx;
    float vig = uv.x*uv.y * intensity;
    return pow(vig, extent);
}

vec4 curtain(vec2 uv) {
    const int NUML = 12;
    vec2 x_w[NUML] = vec2[] ( V(0.686,0.004), V(0.325,0.004),
                              V(0.716,0.002), V(0.756,0.0025), V(0.816,0.003), V(0.906,0.003), V(0.976,0.003),
                              V(0.299,0.002), V(0.265,0.0025), V(0.205,0.003), V(0.125,0.003), V(0.025,0.003) );
    for (int i=0; i<NUML; i++) {
        float xd = abs(uv.x - x_w[i].x);
        if (xd < x_w[i].y) { return vec4(0.); }
    }
    return vec4(1.);
}

void censor(inout vec4 fc, vec2 uv, vec2 p) {
    float asp = iResolution.y/iResolution.x;
    float xd = abs(uv.x - p.x + (uv.y - p.y)*asp);
    float xd2 = abs(uv.x - p.x - (uv.y - p.y)*asp);
    float yd = abs(uv.y - p.y);
    if ((xd < 0.007 || xd2 < 0.007) && yd < 0.024) { fc = vec4(0.75,0.,0.,0.); }
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);

	vec2 uv = fragCoord / iResolution.xy;

    if (uv.x>0.686 || uv.x < 0.325) fragColor=vec4(1.); //vec4(1.,0.9,0.9,0.);
    fragColor=min(fragColor, curtain(uv)); // +sin(iTime*4.5)*.002

    if (iMouse.z < 0.5) {
        censor(fragColor, uv, vec2(0.524, 0.634));
        censor(fragColor, uv, vec2(0.424, 0.663));
        censor(fragColor, uv, vec2(0.424, 0.313));
    }

	fragColor *= 1.0 - 0.03 * length(fragCoord / iResolution.xy - 0.5);
    fragColor *= vignette(fragCoord, 0.04, 95.0);
}

