#define MAX 5.
#define MIN 0.007
#define STEP 64
#define PI 3.14159265
#define LR 100.

float dist(vec3 p);


vec3 surfNormal(vec3 p){
    vec2 t = vec2(MIN,0);
	float d = dist(p);

	return normalize(d - vec3(
		dist(p-t.xyy),
		dist(p-t.yxy),
		dist(p-t.yyx)
	));
}

float cubeSDF(vec3 p,vec3 pos,vec3 size){
    vec3 c = abs(p-pos)-size;
    return length(max(c,0.)) + min(0.,max(c.x,max(c.y,c.z)));
}

float dist(vec3 p){
    float d = MAX;
    d = min(d,length(p-vec3(0,-.3,-1))-.6);
    d = min(d,length(p-vec3(.4,-.5,1.7))-.2);
    d = min(d,cubeSDF(p,vec3(0,-.7,0),vec3(1.1,.05,2)));
    d = min(d,cubeSDF(p,vec3(-.7,-.1,1.5),vec3(.1,.7,.4)));
    d = min(d,cubeSDF(p,vec3(.5,-.5,.5),vec3(.3,.2,.05)));
    d = min(d,cubeSDF(p,vec3(-.6,-.5,-.5),vec3(.5,.3,.5)));
    return d;
}

vec4 march(vec3 ro,vec3 rp, vec3 rd){
    float travel = length(rp-ro);
    for(int i = 0;i<STEP;i++){
        rp = ro + travel*rd;
        float d = dist(rp);
        travel += d;
        if(d <= MIN)break;
        if(d >= MAX){
            break;
        }
    }
    return vec4(rp,travel);
}

