// Particle Buffer


#define PRESET 3
// 0 : Boring
// 1 : Squiggly
// 2 : High Velocity
// 3 : Dendrites
// 4 : Stable
// 5 : Bipolar                          // I asked friends to help me find the word that means a system that
                                        // will converge to a stable state, but explode at tiny perturbations
                                        // and this is the suggestion I received. It works Â¯\_(ã)_/Â¯
// 6 : Angular
// 7 : Cellular
// 8 : Lattice

// Alright that's enough. Have fun exploring :)


#if PRESET == 0
// Less than one gets funky
const float searchRange = 1.;
const float searchAngle = .4;
const float turnAngle = .02;
#endif

#if PRESET == 1
const float searchRange = 1.;
const float searchAngle = pi/3.;
const float turnAngle = .02;
#endif

#if PRESET == 2
const float searchRange = 5.;
const float searchAngle = .1;
const float turnAngle = .05;
#endif

#if PRESET == 3
const float searchRange = 5.;
const float searchAngle = .1;
const float turnAngle = .2;
#endif

#if PRESET == 4
const float searchRange = 1.;
const float searchAngle = 1.;
const float turnAngle = .2;
#endif

#if PRESET == 5
const float searchRange = 2.;
const float searchAngle = 2.*pi/3.;
const float turnAngle = 2.*pi/3.;
#endif

#if PRESET == 6
const float searchRange = 2.;
const float searchAngle = pi/2.;
const float turnAngle = pi/2.;
#endif

#if PRESET == 7
const float searchRange = 1.;
const float searchAngle = 0.01;
const float turnAngle = pi/7.;
#endif

#if PRESET == 8
const float searchRange = 50.;
const float searchAngle = .01;
const float turnAngle = .01;
#endif


const float randomization = 5e-3;
// Without this they'll converge to a stable state and then never do anything fun again :(






void mainImage( out vec4 O, vec2 U ){

    vec2 r = iResolution.xy;
    vec2 uv = U/r;

    float aspect = r.x/r.y;

    // if (uv.x * scale > 1. || uv.y * scale > 1.) return;
    // Prevent simulation of particles out of bounds
    // If we allow it then can resize at runtime


    if (iFrame < 30){

        uvec3 a = uvec3(U, iDate.w);
        O.xy = (circleSample(a))*.25 * vec2(1, aspect) + .5;
        O.zw = normalize(hash(a).xy*2.-1.) * rot(pi/2.7);
        return;

    }

    O = texture(iChannel0, uv);

    vec2 pos = O.xy;        
    vec2 vel = O.zw;

    const int rayCount = 3;
    float rays[rayCount];

    float x = 20.;

    for (int ray = 0; ray < rayCount; ray++){
        vec2 rayDir = vel * rot(-searchAngle+float(ray)*searchAngle);

        x = texture(iChannel2, pos + rayDir*scale/r.x*searchRange).x;

        rays[ray] = x;
    }

    vec3 h = hash(uvec3(U, iDate.w*1000.));
    if (h.x < randomization){ // Crazy tiny amount of divergents.
                     // That's all it takes to break up regularity with millions of particles :) 
        vel *= rot(h.y);
    } else {
        if (rays[0] > rays[2]){
            // Left
            vel *= rot(-turnAngle);
        } else if (rays[2] > rays[0]) {
            // Right
            vel *= rot( turnAngle);
        } else {
            // Centre

            //vel *= rot(h.y);
        }
    }

    pos += vel * scale/r.x * 2e-2 * searchRange * vec2(1, aspect);

    pos = mod(pos, 1.);

    O = vec4(pos, vel);

}

