void push(inout vec4 O, vec2 uv, vec2 res, float me, float new){
    ivec2 iuv = ivec2(floor(uv*res));
    ivec2 ires = ivec2(floor(res));

    float e = encrypt(uv, res);
    if (new < me && e != O.x && e != O.y && e != O.z && e != O.w){
        O.w = O.z; O.z = O.y; O.y = O.x;
        O.x = e;
    }

}

