#define R iResolution.xy
#define A(i) texelFetch(iChannel0,ivec2(i,0),0);
#define B(i) texelFetch(iChannel1,ivec2(i,0),0);
#define C(U) texture(iChannel2,(U)/R)
#define D(U) texture(iChannel3,(U)/R)
#define Main void mainImage(out vec4 Q, vec2 U)
#define N 100.
vec3 hash33(vec3 p3)
{ // Dave H
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);

}
float ln (vec2 p, vec2 a, vec2 b) {
	return length(p-a-(b-a)*clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.));
}
float absmin(float a, float b) {
	if (b>0.) return min(a,b);
    return a;
}
float intersect (vec2 coes) {
	float i=1e4;
    float det = coes.x*coes.x-4.*coes.y;
    if (det < 0.) return i;
    det =sqrt(det);
    i = absmin(i,0.5*(-coes.x+det));
    i = absmin(i,0.5*(-coes.x-det));
    return i;
}
float plane (vec3 p, vec3 d, vec3 c, vec3 n) {
// dot(p+d*i-c,n) = 0
// d.n i =-(p-c).n
    return absmin(-dot(p-c,n)/dot(d,n),1e9);
}
float sphere (vec3 p, vec3 d, vec3 c, float r) {
	c = p-c;
    return intersect(vec2(2.*dot(c,d),dot(c,c)-r*r));
}
vec3 tubenor (vec3 p, vec3 a, vec3 b) {
    float d = dot(p-a,b-a)/dot(b-a,b-a);
    vec3 q = a + (b-a)*d;
    q = p-q;
    if (length(q)>0.) return normalize(q);
    else return vec3(0);
}
float tube (vec3 p, vec3 d, vec3 a, vec3 b, float r) {
	vec3 ab = b-a,
         ap = p-a;
    float 
        abd = dot(ab,d),
        abab = dot(ab,ab),
        apd = dot(ap,d),
        abap = dot(ab,ap),
        apap = dot(ap,ap),
        l = sqrt(abab);
   float x = intersect(vec2 (
        2.*(apd-abap*abd/abab),
        apap-r*r-abap*abap/abab
    )/(1.-abd*abd/abab));

    vec3 i = p+d*x;
    float t = dot(i-a,ab)/l;
    if (t > 0. && t < l) return x;
    else return 1e4;

}

