buffer_b_code:
// velocity update - random forces 
Main {
    if (U.x>N || U.y > 2.) discard;
    Q = B(U.x);
    vec4 p = A(U.x);
    vec3 f = vec3(0);

    Q.xyz += f;
    Q.y += -2e-5;
    vec3 h = hash33(floor(12000.*p.xyz))*2.-1.;
    float hl = length(h);
    float l = length(Q.xyz);
    if (hl>0.&&l>0.) {
        float d = min(0.,dot(normalize(h),normalize(Q.xyz)));
        vec3 v = Q.xyz + .01*d*normalize(h)*pow(hl,2.);
        if (length(v)>0.)
        Q.xyz = .985*l*normalize(v);
    }
    if (iFrame < 1) {
        Q = vec4(0,3.3e-2,0,0);
    }
}

buffer_c_code:
// draw tubes and spheres and accumulate the image
Main {


    vec3 p = vec3(0,0,-1.1),
         d = normalize(vec3(2.*(U-0.5*R)/R.y,1));
    if (iFrame < 1) {
        Q = vec4(0,0,0,1e9);
        float i = plane(p,d,vec3(0,0,2),vec3(-.5,0,1));
        if (i > 0. && i < Q.w) Q = vec4(0,1,1,i);
        i = plane(p,d,vec3(0,-1,0),vec3(0,1,0));
        if (i > 0. && i < Q.w) Q = vec4(1,1,0,i);
        return;
    }
    Q = C(U);
    #define rad max(.1*exp(-float(iFrame)*3e-2),.001)
    for (float n = 0.; n < N; n++) {
        vec4 a = A(n);
        vec4 b = B(n);
        float i = tube(p,d,a.xyz,a.xyz+b.xyz,rad);
        if (i>0.&&i<Q.w) {
            vec3 q = p + d*i;
            Q.xyz = (reflect(d,tubenor(q,a.xyz,a.xyz+b.xyz)).x*0.5+0.8)*(0.5+0.5*sin(float(iFrame)*.02+vec3(1,2,3)));
            Q.w = i;
        }
        i = sphere(p,d,a.xyz,rad);
        if (i>0.&&i<Q.w) {
            vec3 q = p + d*i;
            Q.xyz = (reflect(d,normalize(q-a.xyz)).x*0.2+0.8)*(0.5+0.5*sin(float(iFrame)*.02+vec3(1,2,3)));
            Q.w = i;
        }
    }
    p = p + d*Q.w;
    d = normalize(vec3(1,1,-1)-p);
    p += .01*d;
    for (float n = 0.; n < N; n++) {
        vec4 a = A(n);
        vec4 b = B(n);
        float i = tube(p,d,a.xyz,a.xyz+b.xyz,rad);
        if (i>0.&&i<1e3) {Q.xyz *= .8;break;}
        i = sphere(p,d,a.xyz,rad);
        if (i>0.&&i<1e3) {Q.xyz *= .8;break;}
    }
}

