    vec2 res = iResolution.xy / 79.0;

    // Calculate new frag coord to fot to calculated resolution
    vec2 frag = fragCoord / res;

    // Calculate letter position
    ivec2 pos = ivec2(frag / vec2(4.0, 6.0));

    // Calculate int pixel position in a single letter
    ivec2 px = ivec2(frag.xy) % ivec2(4, 6);

    // Calculate letter number
