#undef TEX
#undef TEXA
#undef T0

vec4 Time;
vec4 FCol = vec4(0);
vec2 UV;

vec3 Light() 			{ return vec3(1); }

#define TEX(name)		vec3 name(vec2 uv); void name() { FCol = vec4(name(fract(UV)), 1); } vec3 name(vec2 uv)
#define TEXA(name)		vec4 name(vec2 uv); void name() { FCol = name(fract(UV)); } vec4 name(vec2 uv)

#define NO_COPY_PASTE	// use as a reminder

