// distance, angle
vec2 circ2(vec2 p, float r, float t) {
    return vec2((length(p) - r) / t * .5 + .5, fract(atan(p.y, p.x) / TAU));
}

void xxsnake_seg(inout vec2 old, vec2 uv, inout vec2 c, float r, float t, float angle) {
    c += sc(angle * TAU).yx * r * 2.;
    if ((fract(angle) - old.y) * sign(angle) < 0.)
    	old = circ2(uv - c, r, t);
    //old = circ2(uv - c, r, t);
}

float cross2d(vec2 a, vec2 b) {
    return a.x * b.y - a.y * b.x;
}

void snake_seg(inout vec3 s, inout float r, vec2 p, vec2 a, vec2 b, vec2 c) {
    r = length(b - a) - r;
    a = normalize(a - b);
    c = normalize(c - b);
    p = safe_normalize(p - b);
    if (cross2d(a, c) > cross2d(p, a)) {
        s.xy = p;
        s.z = length(p) - r;
    }
}

