// base_trim/basemetalsupport
TEX(bmtsprt) {
	float
		b = FBMT(uv, vec2(7, 3), .9, 3., 4),
		h = uv.y + b * .04;
	vec3 c = mix(RGB(50, 40, 34), RGB(93, 92, 88), b * b);
	c *= 1.
		+ .9 * ls(.07, .04, abs(uv.y - .44))
		- .4 * ls(.08, .05, abs(uv.y - .7))
		- .5 * tri(.34, .05, uv.y)
		- .3 * tri(.7, .04, uv.y)
		+ .7 * tri(.01, .03, uv.y)
		+ 1.5 * tri(.89, .1, h)
		+ 1.3 * tri(.5, .04, h)
		+ .9 * tri(.18, .04, h)
		;
	return c;
}

