float mtltech(vec2 uv) {
    float b = NT(uv, vec2(64)), f = 0., d = 1e6;
    for (/**/; f < 11.; ++f)
        d = smin(d, abs(length(.5 - abs(uv - R2(f))) - mix(.36, .29, R1(f + .7))) - mix(.015, .03, b), .01);
    return d * 1e2;
}

vec3 mtltech_d(vec2 uv) {
    vec3 s, p;
    for (int i=0; i<3; ++i) {
        p = vec3(uv, 0);
        p[i] += 1e-4;
        s[i] = mtltech(p.xy);
    }
    return vec3(normalize(s.xy - s.z), s.z);
}


