// main menu banner (menu shader)
void menubnr_m() {
    vec2 uv = UV - .5;
	uv.y = (uv.y - .16) * 7.;
    FCol = mx(abs(uv + vec2(0, .23)) / vec2(.55, .2)) > .4 ?
            vec4(0) :
            menubnr(uv + .51).w
        	* FBMT(uv - vec2(.2 * N(Time.x), Time.x), vec2(7, 3), .7, 2., 4)
        	* vec4(2, 1, .3, 0);
    if (mx(abs(uv - vec2(0, .15)) / vec2(.77, 1)) > .4)
        return;

    float
        n = H(uv * 133.7 + Time.x),
        k = 1./48.,
        s = pow(1.2, k),
        i = n * k
	;
    vec2 p;
    uv /= mix(1., s, n);
    for (; i < 1.; i += k) {
		uv.x += (N(.37 * Time.x) - .5) * k / 48.;
		uv.y += (N(.21 * Time.x) - .5) * k / 32.;
        p = uv /= s;
        p.y -= i * .15;
        p.x *= 1. + p.y * .15;
        vec4 c = menubnr(p + .5);
		FCol += c.x * (1. - i) * k * vec4(32, 16, 4, 0);
	}
}

