////////////////////////////////////////////////////////////////

float slide_alpha(float slide, float t, float current, float next) {
    if (slide == current) return 1. - t;
    if (slide == next) return t;
    return 0.;
}

void slideshow() {
    float b = FBMT(gl_FragCoord.xy / iResolution.y, vec2(7), .7, 2., 4);
    float t = dot(gl_FragCoord.xy / iResolution.xy, vec2(1, -.375)) / 8. + .125 + Time.x / 1.5 + b * .125;

    const bool DUPLICATE_LAST_SLIDE = false;
    if (DUPLICATE_LAST_SLIDE) {
        t = mod(t, NUM_SLIDES + 1.);
        t -= sat(t - NUM_SLIDES + 1.);
    } else {
        t = mod(t, NUM_SLIDES);
    }

    float current = floor(t);
    float next = current + 1.;
    if (next == NUM_SLIDES)
        next = 0.;
    t -= current;
    t = smoothstep(.875, 1., t);

    vec4 c = vec4(0);
    float slide_index = 0., alpha = 0.;

    #define DRAW_SLIDE(name)\
        alpha = slide_alpha(slide_index++, t, current, next);\
        if (alpha > 0.) {\
            name();\
            c += FCol * alpha;\
        }

    SLIDES(DRAW_SLIDE)

    #undef DRAW_SLIDE

    FCol = c;
}

