#define ARG0_(a,b,c)		a
#define ARG1_(a,b,c)		b
#define ARG2_(a,b,c)		c

#define ARG0(x)				ARG0_(x) // force x macro evaluation
#define ARG1(x)				ARG1_(x) // force x macro evaluation
#define ARG2(x)				ARG2_(x) // force x macro evaluation

#define TEXTURE_NAME		ARG0(SHOW_TEXTURE)
#define TEXTURE_SIZE		vec2(ARG1(SHOW_TEXTURE), ARG2(SHOW_TEXTURE))
#define ASPECT				(float(ARG2(SHOW_TEXTURE)) / float(ARG1(SHOW_TEXTURE)))

////////////////////////////////////////////////////////////////

vec3 checker() {
    ivec2 addr = (ivec2(gl_FragCoord.xy) >> 3) & 1;
    return (addr.x ^ addr.y) == 1 ? vec3(.5) : vec3(.25);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    State state;
    load(iChannel0, state);

    Time = vec4(iTime,0,0,0);
    Time.x += state.time_offset;

    vec2 coord = fragCoord;
    vec2 screen_center = iResolution.xy * .5;
    coord -= screen_center;
    coord *= state.zoom;
    if (!state.center)
    	coord += screen_center;

    vec2 scale_ratio = iResolution.xy / TEXTURE_SIZE;
    scale_ratio /= mn(scale_ratio);

    vec2 uv = coord / iResolution.xy + state.offset;
    uv *= scale_ratio;

    uv = uv * INITIAL_UV_SCALE + INITIAL_UV_OFFSET;
    if (state.center)
    	uv += .5;
    UV = uv;

    TEXTURE_NAME();

    if (!state.tile && mx(abs(uv - .5)) > .5)
        FCol = vec4(0);

    switch (state.alpha_mode) {
	default:
	case VIEW_BASE_COLOR:
    	fragColor = FCol;
       	break;

	case VIEW_ALPHA:
        fragColor = vec4(FCol.aaa, 1);
        break;

	case VIEW_BLACK_MATTE:
        fragColor = vec4(FCol.rgb * FCol.a, 1);
        break;

	case VIEW_CHECKER:
        // premultiplied alpha
        fragColor = vec4(checker() * (1. - FCol.a) + FCol.rgb, 1);
        break;

	case VIEW_CHECKER_TEST:
        fragColor = vec4(FCol.a < 0.5 ? checker() : FCol.rgb, 1);
        break;
    }
}

