const int
	KEY_A = 65, KEY_B = 66, KEY_C = 67, KEY_D = 68, KEY_E = 69, KEY_F = 70, KEY_G = 71, KEY_H = 72, KEY_I = 73, KEY_J = 74,
	KEY_K = 75, KEY_L = 76, KEY_M = 77, KEY_N = 78, KEY_O = 79, KEY_P = 80, KEY_Q = 81, KEY_R = 82, KEY_S = 83, KEY_T = 84,
	KEY_U = 85, KEY_V = 86, KEY_W = 87, KEY_X = 88, KEY_Y = 89, KEY_Z = 90,

    KEY_0 = 48, KEY_1 = 49, KEY_2 = 50, KEY_3 = 51, KEY_4 = 52, KEY_5 = 53, KEY_6 = 54, KEY_7 = 55, KEY_8 = 56, KEY_9 = 57,

	KEY_PLUS		= 187,
	KEY_MINUS		= 189,
	KEY_EQUAL		= KEY_PLUS,

    // firefox...
    KEY_PLUS_FF		= 61,
    KEY_MINUS_FF	= 173, 

	KEY_SHIFT		= 16,
	KEY_CTRL		= 17,
	KEY_ALT			= 18,

    KEY_ESC			= 27,

    KEY_BKSP 		=  8,
    KEY_TAB			=  9,
	KEY_END			= 35,
	KEY_HOME		= 36,
	KEY_INS			= 45,
	KEY_DEL			= 46,
	KEY_INSERT		= KEY_INS,
	KEY_DELETE		= KEY_DEL,

	KEY_ENTER		= 13,
	KEY_SPACE 		= 32,
	KEY_PAGE_UP 	= 33,
	KEY_PAGE_DOWN 	= 34,
	KEY_PGUP 		= KEY_PAGE_UP,
	KEY_PGDN 		= KEY_PAGE_DOWN,

	KEY_LEFT		= 37,
	KEY_UP			= 38,
	KEY_RIGHT		= 39,
	KEY_DOWN		= 40,

    KEY_PAD_PLUS	= 107,
    KEY_PAD_MINUS	= 109,
    KEY_PAD_SLASH	= 111,
    KEY_SLASH		= 191
;

float key_down(int key) { return texelFetch(iChannel1, ivec2(key, 0), 0).x; }
float key_pressed(int key) { return texelFetch(iChannel1, ivec2(key, 1), 0).x; }

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    ivec2 addr = ivec2(fragCoord);

	State state;
    if (iFrame == 0) {
        reset(state);
        if (iTime > 5.) { // thumbnail
            state.time_offset -= 10.;
        }
    } else {
        fragColor = texelFetch(iChannel0, addr, 0);
        load(iChannel0, state);
    }

    float alt = key_down(KEY_ALT);
    float zoom =
        key_down(KEY_PAD_PLUS) - key_down(KEY_PAD_MINUS) +
        max(key_down(KEY_PLUS), key_down(KEY_PLUS_FF)) - max(key_down(KEY_MINUS), key_down(KEY_MINUS_FF));
    zoom = exp2(-zoom * iTimeDelta * 3.);

    state.zoom *= zoom;

    if (max(key_pressed(KEY_PAD_SLASH), key_pressed(KEY_SLASH)) > 0.) {
        state.zoom = 1.;
        state.offset = vec2(0);
    }

    //if (key_pressed(KEY_HOME) > 0.)
    //    state.offset = vec2(0);

    for (int i = 1; i < 9; ++i)
        if (key_pressed(KEY_0 + i) > 0.)
	        state.zoom = alt > 0. ? float(i) : 1. / float(i);

	if (key_pressed(KEY_M) > 0.)
        state.alpha_mode = (state.alpha_mode + 1) % ALPHA_MODE_COUNT;

	if (key_pressed(KEY_T) > 0.)
        state.tile = !state.tile;

	if (key_pressed(KEY_C) > 0.)
        state.center = !state.center;

    if (iMouse.z > 0. && state.time_offset == 0.) {
        vec2 moved = iMouse.xy - abs(iMouse.zw);
        vec2 delta = moved - state.prev_mouse;
        if (key_down(KEY_CTRL) > 0.)
            state.zoom *= exp2(-delta.y / iResolution.y * 2.);
        else if (key_down(KEY_ALT) > 0.)
        	state.time_offset += delta.x / iResolution.x;
        else
        	state.offset -= delta / iResolution.xy * state.zoom;
    	state.prev_mouse = moved;
    } else {
        state.prev_mouse = vec2(0);
    }

    state.offset.x += (key_down(KEY_RIGHT) - key_down(KEY_LEFT)) * iTimeDelta * state.zoom;
    state.offset.y += (key_down(KEY_UP) - key_down(KEY_DOWN)) * iTimeDelta * state.zoom;

    store(fragColor, fragCoord, state);
}

