////////////////////////////////////////////////////////////////////////////////
//
// Complete Graph II
// Matthew Arcus, mla, 2020
//
// Now with colours.
//
// Take N points in a circle, draw a line between each pair of
// points. The obvious way to do this in a fragment shader is to test
// the pixel against every point pair, but this gets slow for larger
// number of points. A better way is to maintain two indexes into the
// points, i and j, and each time around the loop, after testing for
// proximity to the line between i and j, increment the index that
// will move the line towards the pixel rather than away (draw a
// diagram if this isn't clear).
//
// We can also draw the lines selectively, for example, for a
// complete bipartite graph.
// 
////////////////////////////////////////////////////////////////////////////////

const int N = 63;  // number of nodes
const int P = 21;  // bipartite block size
int Q;             // sub block size
const float T = 15.0; // Cycle time
const float X = 6.0; //0.1*float(N);  // Speed (N/X should be 10 seconds or so)
bool triangular = false; // Colour to show triangular numbers

bool select(int i, int j, int cycle) {
  if (Q == 0) return true; // Complete graph
  // For a bipartite graph, divide up into blocks of P points, the
  // first Q points of each block are connected to every point in the
  // last P-Q of each block.
  return (i%P < Q) != (j%P < Q);
}

vec3 diagram(vec2 p) {
  p = rotate(p,0.01); // Rotate slightly to reduce artefacts
  float lwidth = fwidth(p.x);
  vec3 col = vec3(0); //1.0-vec3(1,1,0.8);
  if (dot(p,p) > 1.0+lwidth) return col;
  int cycle = int(iTime/T);
  Q = cycle%(P/2+1);
  float k = float(N);
  k *= smoothstep(0.0,k,min(k,X*mod(iTime,T)));
  float d2min = 1e8, rmin;
  float offset = 0.0;
  if (Q != 0) offset = 0.5*float(Q-1); // Centre on Q block
  for (float i = 0.0, j = 1.0; j < k; ) {
    float ii = 2.0*PI/k*(i-offset);
    float ij = 2.0*PI/k*(j-offset);
    vec2 q = vec2(sin(ii),cos(ii));
    vec2 r = vec2(sin(ij),cos(ij));
    if (select(int(i),int(j),cycle)) {
      float d2 = segment2(p,q,r);
      if (d2 < d2min) {
        d2min = d2;
        if (!triangular) rmin = 1.0-0.25*dot(r-q,r-q);
        else rmin = (abs(i-j)-1.0)/k;
      }
    }
    vec2 n = r-q;
    if (dot(p-q,vec2(-n.y,n.x)) > 0.0) i++;
    else j++;
  }
  col = mix(hsv2rgb(0.0*iTime+pow(rmin,1.0),1.0,1.0),col,smoothstep(0.0,lwidth,sqrt(d2min)));
  return col;
}

void mainImage(out vec4 fragColor, vec2 fragCoord ) {
  vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
  if (iMouse.z > 0.0) {
    vec2 mouse = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
    p -= mouse; // Centre on mouse
    p *= 0.55*iResolution.y/iResolution.x; // And zoom
  }
  vec3 col = diagram(p);
  fragColor = vec4(pow(col,vec3(0.4545)),1);
}

