const float pi = 3.1415927;

#define VAR_ZERO min (iFrame, 0)

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

vec2 SphGrid (vec3 p)
{
  vec3 q;
  vec2 a, sc, nSeg;
  float dMin, d1, d2, r;
  nSeg = vec2 (16., 19.);
  sc = sin (0.07 * 2. * pi / nSeg + vec2 (0., 0.5 * pi));
  q = p.yxz;
  r = length (q.yz);
  a = 2. * pi * (floor (nSeg * atan (q.zx, - vec2 (q.y, r)) / (2. * pi)) + 0.5) / nSeg;
  q.yz = Rot2D (q.yz, a.x);
  d1 = dot (vec2 (q.y, abs (q.z)), sc);
  q.yz = Rot2D (vec2 (r, q.x), a.y);
  d2 = dot (vec2 (q.y, abs (q.z)), sc);
  return vec2 (d1, d2);
}

float ObjDf (vec3 p)
{
  vec3 q, qq;
  vec2 d2;
  float dMin, d, dh, s, zp, rh, cvOrd, a, flshLen;
  dMin = dstFar;
  q = p;
  rh = length (q.xz);
  q.y -= 2. * psgHt + psgWd + 2. * ringWd;
  d2 = SphGrid (q);
  s = abs (domeRd - length (q));
  d = max (SmoothMax (s - wThk, min (max (d2.x, oculRd - rh), d2.y), 0.02), - q.y);
  d = min (d, max (max (s - 0.3 * wThk, oculRd - rh), - q.y));
  DMINQ (idDom);
  q.xz = mix (q.xz, q.zx, step (abs (q.z), abs (q.x)));
  q.z = abs (q.z);
  q.y -= - psgWd - 2. * ringWd;
  zp = abs (mod (q.z + pcGap, 2. * pcGap) - pcGap);
  d = max (max (abs (abs (q.x) - psgWd + 3. * wThk) - 4.5 * wThk,
     abs (abs (q.y + psgHt) - psgHt) - 0.5 * wThk), q.z - psgLen);
  DMINQ (idPilB);
  qq = q;
  q = vec3 (abs (q.x) - psgWd + 5. * wThk, q.y + psgHt, abs (zp) - pcGap);
  d = max (PrCylDf (q.xzy, 1.5 * wThk, psgHt), qq.z - psgLen);
  DMINQ (idPil);
  q = qq;
  dh = length (vec2 (q.x, max (q.y, 0.))) - psgWd;
  d = max (SmoothMin (abs (dh) - wThk, max (abs (dh) - 1.7 * wThk, pcGap - zp - 0.02), 0.02),
     domeRd - rh);
  d = max (max (d, min (min (0.1 - length (vec2 (max (q.y + 0.4 * psgHt, 0.), zp)), q.y + 1.6 * psgHt),
     0.3 * psgLen - abs (q.z - 0.6 * psgLen))), max (q.z - psgLen, - 2. * psgHt - q.y));
  DMINQ (idPsg);
  d = max (dh - 4. * wThk, abs (q.z - psgLen - 2. * wThk) - 2. * wThk);
  d = max (max (d, - dh - 7.5 * wThk), - 2. * psgHt - 0.5 * wThk  - q.y);
  DMINQ (idEnt);
  d = max (abs (q.x) - psgWd + 7.5 * wThk, 0.);
  d = max (d, max (q.z - psgLen - 4. * wThk, 0.));
  d = max (d, max (abs (q.y + 2. * psgHt + 0.25 * wThk) - 0.25 * wThk, 0.));
  DMINQ (idFlor);
  d = max (max (abs (domeRd - rh) - wThk, abs (q.y - 4. * ringWd) - 2. * ringWd - wThk), - wThk - dh);
  d = max (d, 0.7 * ringWd - length (vec2 (q.x, q.y - 5. * ringWd - wThk)));
  DMINQ (idDomB);
  d = PrSphDf (q - vec3 (0., 4. * ringWd + 2. * wThk, domeRd - wThk), 0.5 * wThk);
  DMINQ (idSpkEx);
  q = p;
  q.y -= 6. * torScl + 2.5 * wThk;
  d = PrCylDf (q.xzy, rTor + 6. * torScl, 9. * torScl);
  if (d < 0.05) { // from "Plasma Coil"
    d = length (abs (abs (vec2 (rh - rTor, q.y)) - 4.5 * torScl)) - 0.5 * torScl;
    DMINQ (idTorL);
    qq = q;
    qq.xz = Rot2D (q.xz, 0.25 * pi);
    d = PrSphDf (vec3 (abs (qq.xz) - rTor + 1.41 * rTorEx, q.y - 4.5 * torScl), 0.8 * torScl);
    DMINQ (idSpkEx);
    d = length (vec2 (rh - rTor, q.y)) - 1.5 * torScl;
    DMINQ (idTorR);
