// Nebula Noise
vec3 nebulanoise(vec3 raypos){
    float density = max(0.0, cyclic3D(raypos)-distance(raypos, vec3(0.0, 0.0, 0.0)));
   // vec3 rgb = vec3(clamp(-distance(raypos, vec3(0.0))+1.0, 0.0, 1.0));
    return vec3(0.239,0.569,1.000)*density;
}

