vec3 directLight(vec3 raypos, float dither){
    vec3 LightColor = vec3(0.5);
    vec3 LightDirection = normalize(vec3(sin(1.),cos(1.), 1.0));
    float distorigin = 0.0;
    vec3 density = vec3(0.0);
    vec3 attenuation = vec3(1.0);
    raypos += dither*LightDirection*STEP_SIZE;
    for(int i=0; i<MAX_MARCHES; i++)
    {
        density = nebulanoise(raypos);
        vec3 absorbance = exp(-ABSORPTION*density*STEP_SIZE);
        attenuation *= absorbance;
        if(length(attenuation) < 0.05) {attenuation *= 0.5; break;}
        raypos = raypos+(LightDirection*STEP_SIZE);
     }
    return LightColor*attenuation;
}

// Compute/March the Ray
vec3 raymarch(vec3 camerapos, vec3 raydir, vec2 coord){
    float distorigin = 0.0;
