#define SIN_15 0.2588190451
#define COS_15 0.96592582628

// New hash based on hash13() from "Hash without Sine" by Dave_Hoskins (https://www.shadertoy.com/view/4djSRW)
float noise(in vec4 p) {
	p = fract(p * 0.1031);
    p += dot(p, p.zwyx + 31.32);
    return fract((p.x + p.y) * p.z - p.x * p.w);
}

float snoise(in vec4 p) {
    vec4 cell = floor(p);
    vec4 local = fract(p);
    local *= local * (3.0 - 2.0 * local);

    float ldbq = noise(cell);
    float rdbq = noise(cell + vec4(1.0, 0.0, 0.0, 0.0));
    float ldfq = noise(cell + vec4(0.0, 0.0, 1.0, 0.0));
    float rdfq = noise(cell + vec4(1.0, 0.0, 1.0, 0.0));
    float lubq = noise(cell + vec4(0.0, 1.0, 0.0, 0.0));
    float rubq = noise(cell + vec4(1.0, 1.0, 0.0, 0.0));
    float lufq = noise(cell + vec4(0.0, 1.0, 1.0, 0.0));
    float rufq = noise(cell + vec4(1.0, 1.0, 1.0, 0.0));
    float ldbw = noise(cell + vec4(0.0, 0.0, 0.0, 1.0));
    float rdbw = noise(cell + vec4(1.0, 0.0, 0.0, 1.0));
    float ldfw = noise(cell + vec4(0.0, 0.0, 1.0, 1.0));
    float rdfw = noise(cell + vec4(1.0, 0.0, 1.0, 1.0));
    float lubw = noise(cell + vec4(0.0, 1.0, 0.0, 1.0));
    float rubw = noise(cell + vec4(1.0, 1.0, 0.0, 1.0));
    float lufw = noise(cell + vec4(0.0, 1.0, 1.0, 1.0));
    float rufw = noise(cell + 1.0);

    return mix(mix(mix(mix(ldbq, rdbq, local.x),
                       mix(lubq, rubq, local.x),
                       local.y),

                   mix(mix(ldfq, rdfq, local.x),
                       mix(lufq, rufq, local.x),
                       local.y),

                   local.z),

               mix(mix(mix(ldbw, rdbw, local.x),
                       mix(lubw, rubw, local.x),
                       local.y),

                   mix(mix(ldfw, rdfw, local.x),
                       mix(lufw, rufw, local.x),
                       local.y),

                   local.z),

               local.w);
}

float fnoise(in vec4 p, in float scale, in float octaves) {
    p *= scale;
    float nscale = 1.0;
    float tscale = 0.0;
    float value = 0.0;

    for (float octave=0.0; octave < octaves; octave++) {
        value += snoise(p) * nscale;
        tscale += nscale;
        nscale *= 0.5;
        p *= 2.0;
    }

    return value / tscale;
}

float mapScene(in vec3 p) {
    float octaves = 4.0;
    float threshold = 0.4;

    if (iMouse.z > 0.0) {
        octaves = iMouse.y / iResolution.y * 4.0;
        threshold = iMouse.x / iResolution.x * 0.9;
    }

    vec3 q = abs(p) - 1.5;
    float bbox = max(q.x, max(q.y, q.z));

    return max(fnoise(vec4(p, iTime * 0.5), 2.0, octaves) - threshold, bbox);
}

vec3 getNormal(in vec3 p) {
    vec3 e = vec3(0.001, 0.0, 0.0);
    return normalize(vec3(mapScene(p + e.xyy) - mapScene(p - e.xyy),
                          mapScene(p + e.yxy) - mapScene(p - e.yxy),
                          mapScene(p + e.yyx) - mapScene(p - e.yyx)));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    vec3 ro = vec3(0.0, 0.0, 5.0), rd = normalize(vec3(uv, -1.0)), p;
    float t = 0.0, d;
    bool hit = false;

    for (int iter=0; iter < 100; iter++) {
        p = ro + rd * t;

        p.y -= 0.3;
        p.yz *= mat2(COS_15, SIN_15, -SIN_15, COS_15);
        float c = cos(iTime), s = sin(iTime);
        p.xz *= mat2(c, s, -s, c);

        d = mapScene(p);
        if (d < 0.001) {
            hit = true;
            break;
        }

        if (t > 10.0) {
            break;
        }

        t += d;
    }

    if (hit) {
        vec3 n = getNormal(p);
        fragColor.rgb += 0.5 + 0.5 * n;
    }
}

#define PI 3.1415926535897932384626433832795
#define TWOPI 6.283185307179586



float rand(vec2 n) { 
    return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(vec2 p){
    vec2 ip = floor(p);
    vec2 u = fract(p);
    u = u*u*(3.0-2.0*u);

    float res = mix(
        mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
        mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
    return res*res;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 st = abs((2.*fragCoord.xy/iResolution.xy*2.-2.));
    //vec2 st = fragCoord.xy/iResolution.xy;
    st *= vec2(length(0.1*st),atan(st.y,st.x/iTime));
    float noise1 = noise(st+0.3*iTime);
    st.x *= iResolution.x/iResolution.y;
    st.x += sin(1.0*st.y+iTime)*0.5+0.5;
   // st.y = sin(2.0*st.x-u_time);
    st *= vec2(length(st.x*st.y*noise1+sin(st.y*iTime)*0.5+0.5),atan(st.y+sin(st.x*iTime*.003)*0.5+-0.5,st.x-sin(iTime*.03*noise1)*0.5+0.5)/PI/8.*noise1);
    float d = length(st-vec2(0.003*(1.*sin(sin(st.y+sin(iTime)*0.01*noise1)))*0.5+0.5,0.18*(sin(st.x+sin(iTime)*0.5+0.5*.01*noise1+0.3*0.5))));
    st.y+= log(abs(sin(iTime*.3)));
    st.x += log(abs(sin(iTime*0.3)));
    float circle = 1.-smoothstep(0.6,0.99,d);

//    for(float i = 0.07; i < 1.0; i += .1){
//    
//        circle += smoothstep(0.06+i, 0.061,d+i);
//    }
    vec3 color = vec3(abs(cos(1.0*st.y/d+iTime)),abs(sin(1.0*st.x/d-iTime)),abs(cos(10.0*st.y-st.x/d-iTime)));
    //white
    fragColor = 1.-vec4(vec3(circle)*color,1.0);
    //black
     // gl_FragColor = vec4(vec3(circle)*color,1.0);
}

const float FLIGHT_SPEED = 6.0;

const float FIELD_OF_VIEW = 1.05;


vec3 getRayDirection(vec2 fragCoord, vec3 cameraDirection) {
    vec2 uv = fragCoord.xy / iResolution.xy;

    const float screenWidth = 1.0;
    float originToScreen = screenWidth / 2.0 / tan(FIELD_OF_VIEW / 2.0);

    vec3 screenCenter = originToScreen * cameraDirection;
    vec3 baseX = normalize(cross(screenCenter, vec3(0, -1.0, 0)));
    vec3 baseY = normalize(cross(screenCenter, baseX));

    return normalize(screenCenter + (uv.x - 0.5) * baseX + (uv.y - 0.5) * iResolution.y / iResolution.x * baseY);
}

vec4 getNebulaColor(vec3 globalPosition, vec3 rayDirection) {
    vec3 color = vec3(0.0);
    float spaceLeft = 1.0;

    const float layerDistance = 10.0;
    float rayLayerStep = rayDirection.z / layerDistance;

    const int steps = 4;
    for (int i = 0; i <= steps; i++) {
      vec3 noiseeval = globalPosition + rayDirection * ((1.0 - fract(globalPosition.z / layerDistance) + float(i)) * layerDistance / rayDirection.z);
      noiseeval.xy += noiseeval.z;


        float value = 0.06 * texture(iChannel0, fract(noiseeval.xy / 60.0)).r;

        if (i == 0) {
            value *= 1.0 - fract(globalPosition.z / layerDistance);
        } else if (i == steps) {
            value *= fract(globalPosition.z / layerDistance);
        }

        color += spaceLeft * 2. * vec3(value, value, value) * vec3(.5, .3, 0.1);
        spaceLeft = max(0.0, spaceLeft - value * 2.0);
    }
    return vec4(color, 1.0);
}

#define S(a, b, t) smoothstep(a, b, t)

float distLine(vec2 p, vec2 a, vec2 b){
  vec2 pa = p - a;
    vec2 ba = b - a;
    float t = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    return length(pa - ba*t);
}

float line(vec2 p, vec2 a, vec2 b){
  float d = distLine(p, a, b);
    float m = S(0.03, 0.01, d);
    float d2 =  length(a - b);
    m *= S(1.2, 0.8, d2) * 0.5 + S(0.05, 0.03, abs(d2 - 0.75));
    return m;
}

float distTriangle(in vec2 p, in vec2 p0, in vec2 p1, in vec2 p2 )
{
  vec2 e0 = p1 - p0;
  vec2 e1 = p2 - p1;
  vec2 e2 = p0 - p2;

  vec2 v0 = p - p0;
  vec2 v1 = p - p1;
  vec2 v2 = p - p2;

  vec2 pq0 = v0 - e0*clamp( dot(v0,e0)/dot(e0,e0), 0.0, 1.0 );
  vec2 pq1 = v1 - e1*clamp( dot(v1,e1)/dot(e1,e1), 0.0, 1.0 );
  vec2 pq2 = v2 - e2*clamp( dot(v2,e2)/dot(e2,e2), 0.0, 1.0 );

    float s = sign( e0.x*e2.y - e0.y*e2.x );
    vec2 d = min( min( vec2( dot( pq0, pq0 ), s*(v0.x*e0.y-v0.y*e0.x) ),
                       vec2( dot( pq1, pq1 ), s*(v1.x*e1.y-v1.y*e1.x) )),
                       vec2( dot( pq2, pq2 ), s*(v2.x*e2.y-v2.y*e2.x) ));

  return -sqrt(d.x)*sign(d.y);
}

float triangle(vec2 p, vec2 a, vec2 b, vec2 c){
  float d = distTriangle(p, a, b, c);
    float m = S(0.03, 0.01, d);
    float d2 =  length(a - b);
    m *= S(1.2, 0.8, d2) * 0.5 + S(0.05, 0.03, abs(d2 - 0.75));
    return m;
}

float N21(vec2 p){
  p = fract(p * vec2(233.34, 851.73));
    p += dot(p, p + 23.45);
    return fract(p.x * p.y);
}

vec2 N22(vec2 p){
  float n = N21(p);
    return vec2(n, N21(p + n));
}

vec2 getPos(vec2 id, vec2 offset){
    vec2 n = N22(id + offset) * iTime;
    return offset + sin(n) * 0.4;
}

float layer(vec2 uv){
  vec2 gv = fract(uv) - 0.5;
    vec2 id = floor(uv);
    vec2 p[9];
    int i = 0;
    for(float y = -1.0; y <= 1.0; y++){
      for(float x = -1.0; x <= 1.0; x++){
          p[i++] = getPos(id, vec2(x, y));
      }    
    }


    float t = iTime * 10.0;
    float m = 0.0;
    for(int i = 0; i < 9; i++){
      m += line(gv, p[4], p[i]);

        vec2 j = (p[i] - gv) * 20.0;
        float sparkle = 1.0 / dot(j, j);

        m += sparkle * (sin(t + fract(p[i].x) * 10.0) * 0.5 + 0.5);

        for(int yi= i + 1; yi < 9; yi++){
        for(int zi= yi + 1; zi < 9; zi++){

                float len1 = abs(length(p[i] - p[yi]));
                float len2 = abs(length(p[yi] - p[zi]));
                float len3 = abs(length(p[i] - p[zi]));

                if((len1 + len2 + len3) < 2.8){
                  m += triangle(gv, p[i], p[yi], p[zi]) * 0.8;
                }
        }
      }
    }
    m += line(gv, p[1], p[3]);
    m += line(gv, p[1], p[5]);
    m += line(gv, p[7], p[3]);
    m += line(gv, p[7], p[5]);

    return m;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    //nebula
    vec3 movementDirection = normalize(vec3(0.01, 0.0, 1.0));

    vec3 rayDirection = getRayDirection(fragCoord, movementDirection);

    vec3 globalPosition = vec3(3.14159, 3.14159, 0.0) + (iTime + 1000.0) * FLIGHT_SPEED * movementDirection;

    fragColor = getNebulaColor(globalPosition, rayDirection);

    // plexus
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    float m = 0.0;
    float t = iTime * 0.1;

    for(float i = 0.0; i < 1.0; i += 1.0 / 4.0){
        float z = fract(i + t);
        float size = mix(10.0, 0.5, z);
        float fade = S(0.0, 0.1, z) * S(1.0, 0.8, z);

        m += layer(uv * size + i * 20.0) * fade;
    }


    vec3 base = vec3(0.5, 0.3, 0.1);
    vec3 col = m * base * 0.1;

    col -= uv.y * 0.5 * base;

    fragColor += vec4(col,1.0);
}

