// "Physically-based SDF" by romainguy:
//https://www.shadertoy.com/view/XlKSDR
//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
vec3 Tonemap_ACES(const vec3 x){
    const float a = 2.51;
    const float b = 0.03;
    const float c = 2.43;
    const float d = 0.59;
    const float e = 0.14;
    return (x * (a * x + b)) / (x * (c * x + d) + e);
}

// https://www.shadertoy.com/view/3ssSz2
//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
float originalSigmoidContrast(float color, float contrast, float mid){
    contrast = contrast < 1.0 ? 0.5 + contrast * 0.5 : contrast;
    float scale_l = 1.0 / mid;
    float scale_h = 1.0 / (1.0 - mid);
    float lower = mid * pow(scale_l * color, contrast);
    float upper = 1.0 - (1.0 - mid) * pow(scale_h - scale_h * color, contrast);
    return color < mid ? lower : upper;
}
vec3 originalSigmoidContrast(vec3 color, float contrast, float mid){
	color.r = originalSigmoidContrast(color.r, contrast, mid);
	color.g = originalSigmoidContrast(color.g, contrast, mid);
	color.b = originalSigmoidContrast(color.b, contrast, mid);
	return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 uv = fragCoord.xy / iResolution.xy;

	vec3 col = texture(iChannel0, uv).rgb;
	col = Tonemap_ACES(col);
	col = sat(col);
	col = originalSigmoidContrast(col, 2.5, .2);
	col = pow(col, vec3(1./2.2));
	fragColor = vec4(col, 1);
}

