// "camera": create camera vectors.
//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
vec3 quat_rotate(vec4 quat, vec3 dir){
	return dir + 2.0 * cross(quat.xyz, cross(quat.xyz, dir) + quat.w * dir);
}void perspectiveCam(vec2 uv){
	vec3 dir = quat_rotate(cam0.quaternion, vec3(0,0,-1)).xzy;
	vec3 up = quat_rotate(cam0.quaternion, vec3(0,1,0)).xzy;
	vec3 pos = cam0.position;
	float fov = cam0.fov;
	vec3 target = pos-dir;

	vec3 cw = normalize(target - pos);
	vec3 cu = normalize(cross(cw, up));
	vec3 cv = normalize(cross(cu, cw));

	mat3 camMat = mat3(cu, cv, cw);
	ray.origin = pos;
	ray.direction = normalize(camMat * normalize(vec3(sin(fov) * uv.x, sin(fov) * uv.y, -cos(fov))));;
}
void camera(vec2 uv){
	perspectiveCam(uv);
}

// Cheap Rotation by las:
// http://www.pouet.net/topic.php?which=7931&page=1
//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
#define R(p, a) p=cos(a)*p+sin(a)*vec2(p.y,-p.x)
vec3 rot(vec3 p,vec3 r){
	R(p.xz, r.y);
	R(p.yx, r.z);
	R(p.zy, r.x);
	return p;
}

// SDF functions
//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
float vmax(vec3 v){
	return max(max(v.x, v.y), v.z);
}

float sdPlane(in vec3 p){
	return p.y;
}

float sdEllipsoid(in vec3 p, in vec3 r){
	return (length(p/r)-1.0)*min(min(r.x,r.y),r.z);
}

float sdCapsule(vec3 p, float r, float c){
	return mix(length(p.xz) - r, length(vec3(p.x, abs(p.y) - c, p.z)) - r, step(c, abs(p.y)));
}

float sdTorus( vec3 p, vec2 t ){
	vec2 q = vec2(length(p.xz)-t.x,p.y);
	return length(q)-t.y;
}

float sdCappedTorus(vec3 p, vec2 r, float per){
	p.x = abs(p.x);
	vec2 sc = vec2(sin(per),cos(per));
	float k = (sc.y*p.x>sc.x*p.z) ? dot(p.xz,sc) : length(p.xz);
	return sqrt( dot(p,p) + r.x*r.x - 2.0*r.x*k ) - r.y;
}

float sdCappedCylinder( vec3 p, vec2 h ){
	vec2 d = abs(vec2(length(p.xz),p.y)) - h;
	return ((min(max(d.x,d.y),0.0) + length(max(d,0.0))))-0.0;
}

float sdPieCylinder( vec3 p, float r, float h, float per ){
	per = mod(per, PI);
	vec2 c = vec2(sin(per),cos(per));

	p.xz=c.y*p.xz+c.x*vec2(p.z,-p.x); // rotation
	p.x = abs(p.x);

	float l = length(p.xz) - r;
	float m = length(p.xz-c*clamp(dot(p.xz,c),0.0, r));
	float x = max(l,m*sign(c.y*p.x-c.x*p.z));

	float y = abs(p.y) - h;

	return ((min(max(x,y),0.0) + length(max(vec2(x,y),0.0))));
}

float sdConeSection( in vec3 p, in float h, in float r1, in float r2 ){
	vec2 q = vec2( length(p.xz), p.y );
	vec2 k1 = vec2(r2,h);
	vec2 k2 = vec2(r2-r1,2.0*h);
	vec2 ca = vec2(q.x-min(q.x,(q.y < 0.0)?r1:r2), abs(q.y)-h);
	vec2 cb = q - k1 + k2*clamp( dot(k1-q,k2)/dot(k2,k2), 0.0, 1.0 );
	float s = (cb.x < 0.0 && ca.y < 0.0) ? -1.0 : 1.0;
	return s*sqrt( min(dot(ca,ca),dot(cb,cb)) );
}

float sdRoundCone( vec3 p, float h, float r1, float r2 ){
	vec2 q = vec2( length(p.xz), p.y+h*.5 );

	float b = (r1-r2)/h;
	float a = sqrt(1.0-b*b);
	float k = dot(q,vec2(-b,a));

	if( k < 0.0 ) return length(q) - r1;
	if( k > a*h ) return length(q-vec2(0.0,h)) - r2;

	return dot(q, vec2(a,b) ) - r1;
}

float sdBox(vec3 p,vec3 b){
	vec3 d=abs(p)-b;
	return length(max(d,vec3(0)))+vmax(min(d,vec3(0.0)));
}

float fOpUnion(in float a,in float b){
	return a<b?a:b;
}

vec4 v4OpUnion(in vec4 a,in vec4 b){
	return a.x<b.x?a:b;
}

float fOpUnionSmooth(float a,float b,float r){
	vec2 u = max(vec2(r - a,r - b), vec2(0));
	return max(r, min (a, b)) - length(u);

	// iq:
	//float h = max(r-abs(a-b),0.0);
	//return min(a, b) - h*h*0.25/r;
}

vec4 v4OpUnionSmooth(vec4 a,vec4 b,float r){
	float h=clamp(0.5+0.5*(b.x-a.x)/r,0.0,1.0);
	float res = mix(b.x,a.x,h)-r*h*(1.0-h);
	return vec4(res, mix(b.yzw,a.yzw,h));
}

vec4 v4OpSubstraction(in vec4 a,in vec4 b){
	float res = max(-a.x, b.x);
	return (res==-a.x)?vec4(-a.x, a.yzw):b;
}

vec4 v4OpSubstractionSmooth( vec4 a,vec4 b,float r){
	vec2 u = max(vec2(r + b.x, r + -a.x), vec2(0));
	float res = min(-r, max(b.x, -a.x))+length(u);
	vec3 m = mix(b.yzw, a.yzw, clamp(abs(-b.x)+abs(res),0.0,1.0)*clamp(r,0.,1.));
	return vec4(res, m);
}

vec4 v4OpIntersection(in vec4 a,in vec4 b){
	float res = max(a.x, b.x);
	return (res==a.x)?a:b;
}

vec4 v4OpIntersectionSmooth( vec4 a,vec4 b,float r){
	vec2 u = max(vec2(r + b.x,r + a.x), vec2(0));
	float res =  min(-r, max(b.x, a.x)) + length(u);
	vec3 m = mix(a.yzw, b.yzw, clamp(a.x-res,0.0,1.0)*r);
	return vec4(res, m);
}

// "Limited Repetition SDF" by iq:
// https://www.shadertoy.com/view/3syGzz
void pRepLimited(inout float p_el, float s, float repetitions ){
#if 0
	float r = round(p_el/s);
	float half_num = (repetitions-1.)/2.;
	r = clamp(r, -half_num, repetitions-half_num);
	p_el-=s*r;
#else
	repetitions -= 1.;
	float offset = 1.-step(.5, mod(repetitions, 2.));
	p_el += s*.5*offset;
	float r = round(p_el/s);
	float half_rep = ceil(repetitions/2.);
	r = clamp(r, -half_rep, repetitions-half_rep);
	p_el-=s*r;
#endif
}

// scene: Shapes.
//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
vec4 sd002(vec3 p){
	float d = MAX_DIST;
	vec4 res = vec4(MAX_DIST, MAT_VOID);
	float bsd = length(p), bsr=10.0;
	if (bsd > 2.*bsr) return vec4(bsd-bsr,MAT_VOID);
	d = sdEllipsoid(p+vec3(-12.0, -1.0, -4.0), vec3(0.99, 0.99, 0.99));
	res = v4OpUnion(vec4(d, MAT_ERNST0), res);

	d = sdBox(p+vec3(-12.0, -1.0, -5.0), vec3(1.0, 1.0, 1.0)-0.01)-0.01;
	res = v4OpSubstractionSmooth(vec4(d, MAT_ERNST0_001), res, 0.02);
	return res;
}
vec4 sd001(vec3 p){
	float d = MAX_DIST;
	vec4 res = vec4(MAX_DIST, MAT_VOID);
	float bsd = length(p), bsr=15.0;
	if (bsd > 2.*bsr) return vec4(bsd-bsr,MAT_VOID);
	d = sdEllipsoid(p+vec3(-12.0, -1.0, -8.0), vec3(0.99, 0.99, 0.99));
	res = v4OpUnion(vec4(d, MAT_ERNST0_005), res);

	d = sdBox(p+vec3(-12.0, -1.0, -9.0), vec3(1.0, 1.0, 1.0)-0.01)-0.01;
	res = v4OpIntersectionSmooth(vec4(d, MAT_ERNST0_001), res, 0.02);
	return res;
}

vec4 sdScene(vec3 p){
	float d = MAX_DIST;
	vec4 res = vec4(MAX_DIST, MAT_VOID);

    vec3 cp001 = p;
	cp001.xyz += vec3(-8.0856, -4.9245, -10.7845);
	pRepLimited(cp001.y, 2.5, 3.);
	pRepLimited(cp001.z, 1.25, 4.);
	pRepLimited(cp001.x, 1.25, 4.);

	res = sd001(p);

	res = v4OpUnionSmooth(sd002(p), res, 0.01);

	d = sdBox(p+vec3(0.0, -1.0, -6.0), vec3(1.0, 1.0, 1.0)-0.01)-0.01;
	d = fOpUnionSmooth(sdBox(p+vec3(3.0, -1.0, -6.0), vec3(1.0, 1.0, 1.0)-0.3)-0.3, d, 0.01);
	res = v4OpUnionSmooth(vec4(d, MAT_ERNST0_003), res, 0.01);

	d = sdCappedCylinder(p+vec3(0.0, -1.0, -9.0), vec2(1.0, 1.0)-0.01)-0.01;
	d = fOpUnionSmooth(sdCappedCylinder(p+vec3(3.0, -1.0, -9.0), vec2(1.0, 1.0)-0.3)-0.3, d, 0.01);
	res = v4OpUnionSmooth(vec4(d, MAT_ERNST0), res, 0.01);

	d = sdCappedTorus(p+vec3(-3.0, -0.5, -12.0), vec2(1.0, 0.5), 2.356);
	res = v4OpUnionSmooth(vec4(d, MAT_ERNST0_004), res, 0.01);

	d = sdCapsule(p+vec3(-3.0, -2.0, 0.0), 1., 1.);
	d = fOpUnionSmooth(sdConeSection(p+vec3(0.0, -1.021, -3.0), 1.0-0.01, 1.02-0.01, 0.0)-0.01, d, 0.01);
	d = fOpUnionSmooth(sdConeSection(p+vec3(3.0, -1.021, -3.0), 1.0-0.1, 1.02-0.1, 0.0)-0.1, d, 0.01);
	res = v4OpUnionSmooth(vec4(d, MAT_ERNST0), res, 0.01);

	d = sdEllipsoid(p+vec3(0.0, -1.0, 0.0), vec3(1.0, 1.0, 1.0));
	res = v4OpUnionSmooth(vec4(d, MAT_ERNST0_001), res, 0.01);

	d = sdEllipsoid(p+vec3(-12.0, -1.0, 0.0), vec3(0.99, 0.99, 0.99));
	res = v4OpUnionSmooth(vec4(d, MAT_ERNST0), res, 0.01);

	d = sdEllipsoid(p+vec3(-3.0, -1.5, -3.0), vec3(1.0, 1.5, 1.0));
	d = fOpUnionSmooth(sdPieCylinder(p+vec3(0.0, -1.0, -12.0), 1.0-0.01, 1.0-0.01, 2.356)-0.01, d, 0.01);
	d = fOpUnionSmooth(sdPieCylinder(p+vec3(3.0, -1.0, -12.0), 1.0-0.3, 1.0-0.3, 2.356)-0.3, d, 0.01);
	res = v4OpUnionSmooth(vec4(d, MAT_ERNST0_001), res, 0.01);

	d = sdPlane(p);
	res = v4OpUnionSmooth(vec4(d, MAT_ERNST0_002), res, 0.01);

	{
        // sdHex
		vec3 tp = p+vec3(-6.0, -1.1, 0.0);
		vec3 trp = rot(p+vec3(-6.0, -1.1, 0.0), vec3(1.5708, 0.0, 0.0));
		vec3 dim = vec3(0.75, 0.75, 1.0);
		float td = MAX_DIST;

		const vec3 k = vec3(-0.866254, 0.5, 0.57735);
		vec2 h = dim.xz;
		trp = abs(trp);
		trp.xy -= 2.0*min(dot(k.xy, trp.xy), 0.0)*k.xy;
		vec2 d2 = vec2(
			 length(trp.xy-vec2(clamp(trp.x,-k.z*h.x,k.z*h.x), h.x))*sign(trp.y-h.x),
			 trp.z-h.y );
		td = min(max(d2.x,d2.y),0.0) + length(max(d2,0.0))-.01;
		d=td;
	}
	res = v4OpUnionSmooth(vec4(d, MAT_ERNST0_005), res, 0.01);

	{
        // sdFakeCapsule
		vec3 tp = cp001;
		vec3 trp = cp001;
		vec3 dim = vec3(0.6342, 1.0, 0.6342);
		float td = MAX_DIST;

		float r = max(dim.x, dim.z);
		float c = dim.y*.5;
		float elg = c-min(.5,c);
		trp.y -= clamp(trp.y,-elg,elg); // Elongation: https://www.shadertoy.com/view/Ml3fWj

		float a = .5*trp.y*trp.y+.5;
		float b = abs(trp.y);
		trp.y = b<1. ? a : b; // Smooth abs

		trp.y -= min(.5,c); // Offset
		td = length(trp)-r;
		d=td;
	}
	res = v4OpUnionSmooth(vec4(d, MAT_ERNST0_006), res, 0.01);

	d = sdRoundCone(p+vec3(-3.0, -2., -6.0), 2., 1., 0.0);
	res = v4OpUnionSmooth(vec4(d, MAT_ERNST0), res, 0.01);

	d = sdTorus(p+vec3(-3.0, -0.5, -9.0), vec2(1.0, 0.5));
	d = fOpUnionSmooth(sdBox(p+vec3(-12.0, -1.0, -1.0), vec3(1.0, 1.0, 1.0)-0.01)-0.01, d, 0.01);
	res = v4OpUnionSmooth(vec4(d, MAT_ERNST0_001), res, 0.01);

	return res;
}

//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
vec4 intersect(){
	float d = 1.;
	vec3  m = MAT_VOID;
	for (int i = ZERO; i < ITERATION+ZERO; i++){
		vec4 res = sdScene(ray.origin + d * ray.direction);
		m = res.yzw;
		res.x *= .5;
		if (abs(res.x) < MIN_DIST || res.x >= MAX_DIST) break;
		d += res.x;
		if (d >= MAX_DIST) break;
	}
	return vec4(d,m);
}

//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
vec3 normal(vec3 p)
{
    // Copy from iq shader.
    // inspired by tdhooper and klems - a way to prevent the compiler from inlining map() 4 times
    vec3 n = vec3(0.0);
    for( int i=ZERO; i<4+ZERO; i++ )
    {
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*sdScene(p+0.0005*e).x;
    }
    return normalize(n);
}

//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
float shadow(vec3 o, vec3 n){
	float mint=lit0.shadowStart;
	float maxt=lit0.shadowEnd;
	float k = lit0.shadowSoft;
	float res = 1.;
	float t=mint;
	float ph = 1e10; // big, such that y = 0 on the first iteration
	for( int i=ZERO; i < ITERATION+ZERO; i++){
		float h = sdScene(o + lit0.direction*t).x;
#if 1
		res = min( res, k*h/t);
#else
		float y = h*h/(2.0*ph);
		float d = sqrt(h*h-y*y);
		res = min( res, k*d/max(0.0,t-y) );
		ph = h;
#endif
		t += h;
		if( res<0.0001 || t>maxt ) break;
	}
	return sat(res);
}

// "Hemispherical SDF AO" by XT95:
// https://www.shadertoy.com/view/4sdGWN
//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
vec3 randomSphereDir(vec2 rnd){
	float s = rnd.x*PI*2.;
	float t = rnd.y*2.-1.;
	return vec3(sin(s), cos(s), t) / sqrt(1.0 + t * t);
}
vec3 randomHemisphereDir(vec3 dir, float i){
	vec3 v = randomSphereDir( vec2(hash11(i+1.), hash11(i+2.)) );
	return v * sign(dot(v, dir));
}
float ambientOcclusion( in vec3 p, in vec3 n, in float maxDist, in float falloff ){
	const int nbIte = 12;
	const float nbIteInv = 1./float(nbIte);
	const float rad = 1.-1.*nbIteInv; //Hemispherical factor (self occlusion correction)

	float ao = 0.0;

	for( int i=ZERO; i<nbIte+ZERO; i++ ){
		float l = hash11(float(i)+maxDist*falloff)*maxDist;
		vec3 aord = normalize(n+randomHemisphereDir(n, l+maxDist+falloff )*rad)*l; // mix direction with the normal// for self occlusion problems!
		ao += (l - max(sdScene( p + aord ).x,0.)) / maxDist * falloff;
	}

	return clamp( 1.-ao*nbIteInv, 0., 1.);
}

// "Physically-based SDF" by romainguy:
//https://www.shadertoy.com/view/XlKSDR
//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
float pow5(float x) {
	float x2 = x * x;
	return x2 * x2 * x;
}
float D_GGX(float linearRoughness, float NoH, const vec3 h) {
	float oneMinusNoHSquared = 1.0 - NoH * NoH;
	float a = NoH * linearRoughness;
	float k = linearRoughness / (oneMinusNoHSquared + a * a);
	float d = k * k * (1.0 / PI);
	return d;
}
float V_SmithGGXCorrelated(float linearRoughness, float NoV, float NoL) {
    float a2 = linearRoughness * linearRoughness;
    float GGXV = NoL * sqrt((NoV - a2 * NoV) * NoV + a2);
    float GGXL = NoV * sqrt((NoL - a2 * NoL) * NoL + a2);
    return 0.5 / (GGXV + GGXL);
}
vec3 F_Schlick(const vec3 f0, float VoH) {
	return f0 + (vec3(1.0) - f0) * pow5(1.0 - VoH);
}
float F_Schlick(float f0, float f90, float VoH) {
    return f0 + (f90 - f0) * pow5(1.0 - VoH);
}
float Fd_Burley(float linearRoughness, float NoV, float NoL, float LoH) {
    // Burley 2012, "Physically-Based Shading at Disney"
    float f90 = 0.5 + 2.0 * linearRoughness * LoH * LoH;
    float lightScatter = F_Schlick(1.0, f90, NoL);
    float viewScatter  = F_Schlick(1.0, f90, NoV);
    return lightScatter * viewScatter * (1.0 / PI);
}
#define specular(lr, noV, noL) D_GGX(lr, noV, noL)

//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
void render(){
	vec4 res = intersect();
	vec3 p = ray.origin + res.x * ray.direction;

	if (res.x>=MAX_DIST){
		renDat.position = vec3(0);
		renDat.albedo = FOG_COL;
		renDat.normal = vec3(0);
		renDat.diffuse = 0.;
		renDat.specular = 0.;
		renDat.shadow = 0.;
		renDat.ao = 0.;
		renDat.depth = 1.;
		renDat.result = FOG_COL;
	}
	else{
		renDat.position = p;
		renDat.albedo = res.yzw;
		renDat.normal = normal(p);

        // I wanted to know taste of physically based lighting,
        // so I copied from romainguy's shader.
        // https://www.shadertoy.com/view/XlKSDR
		vec3 h = normalize(-ray.direction + lit0.direction);
		float NoV = abs(dot(renDat.normal, -ray.direction)) + 1e-5;
		float NoL = sat(dot(renDat.normal, lit0.direction));
		float NoH = sat(dot(renDat.normal, h));
		float LoH = sat(dot(lit0.direction, h));
		float roughness = .5;
		float metallic = .0;
		float intensity = .01;

        if(distance(MAT_ERNST0, renDat.albedo)<.1){
            roughness = .3;
            intensity = .0;
        }
        if(distance(MAT_ERNST0_002, renDat.albedo)<.1){
            roughness = .3;
            intensity = .0;
            const float CSIZE = .5;
            renDat.albedo += .05*max(step(.5, fract(p.x*CSIZE)), step(.5, fract(p.z*CSIZE)))*max(step(.5, fract(p.x*CSIZE+.5)), step(.5, fract(p.z*CSIZE+.5)));
        }
        if(distance(MAT_ERNST0_003, renDat.albedo)<.1){
            roughness = .3;
            intensity = .1;
        }
        if(distance(MAT_ERNST0_001, renDat.albedo)<.1){
            roughness = .2;
            intensity = .05;
        }
        float linearRoughness = roughness * roughness;

		float lamb1 = sat(dot(renDat.normal, lit0.direction))*(1./PI);
		float lamb2 = sat(dot(renDat.normal, lit1.direction))*(1./PI);

		renDat.shadow = shadow(p+renDat.normal*.0001, renDat.normal);

		renDat.ao = ambientOcclusion(p, renDat.normal, .1, .5);
		renDat.ao += ambientOcclusion(p, renDat.normal, .5, .5);
		renDat.ao += ambientOcclusion(p, renDat.normal, 2., 2.);
		renDat.ao += ambientOcclusion(p, renDat.normal, 4., 2.);
		renDat.ao = smoothstep(0., 4., renDat.ao);

		renDat.diffuse = lamb1;//Fd_Burley(linearRoughness, NoV, NoL, LoH);
		renDat.diffuse *= renDat.shadow;
		renDat.result = mix(vec3(0), lit0.color, renDat.diffuse);

		renDat.diffuse += lamb2*renDat.ao;
		renDat.result += mix(vec3(0), lit1.color, lamb2*renDat.ao);

		renDat.diffuse += renDat.ao*AMB_STRENGTH;
		renDat.result += mix(vec3(0), AMB_COL, renDat.ao*AMB_STRENGTH);
		renDat.result*= renDat.albedo;

		vec3 f0 = .5*(1. - metallic) + renDat.albedo * metallic;
		vec3  F = F_Schlick(f0, LoH);
		float V = V_SmithGGXCorrelated(linearRoughness, NoV, NoL);
		renDat.specular = specular(linearRoughness, NoH, h)*intensity;
		renDat.result = mix(renDat.result, renDat.result+lit0.color, renDat.specular*renDat.shadow*1.)*F;

		renDat.depth = distance(ray.origin, p)/MAX_DIST;
		renDat.result = mix(renDat.result, FOG_COL, sat(pow(renDat.depth+FOG_START, FOG_POW)));
	}
}

//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
void mainImage(out vec4 fragColor, in vec2 fragCoord){
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 offset = u2s(hash22(fragCoord+iTime))/iResolution.xy*.5; // AA: Offset coord for each frame.
    uv += offset; //

	uv = (uv*2.-1.)*iResolution.y/iResolution.x;
	uv.x *= iResolution.x / iResolution.y;

	init();
	camera(uv);
	render();

	fragColor = vec4(renDat.result, 1.);
}

