	vec2 uv = fragCoord/iResolution.xy;

    // Cell
	float y = texture(iChannel0,uv).x;
    // food
    float f = texture(iChannel0,uv).z;

    // sum of surrounding y's
    float s = 1./16. * texture(iChannel0,uv + vec2(-du,-dv)).x +
              3./16.  * texture(iChannel0,uv + vec2(-du,0)).x + 
              1./16. * texture(iChannel0,uv + vec2(-du,dv)).x + 
              3./16.  * texture(iChannel0,uv + vec2(0,-dv)).x + 
              3./16.  * texture(iChannel0,uv + vec2(0,dv)).x +
              1./16. * texture(iChannel0,uv + vec2(du,-dv)).x +
              3./16.  * texture(iChannel0,uv + vec2(du,0)).x + 
              1./16. * texture(iChannel0,uv + vec2(du,dv)).x;

    // Grow cell when either enough food and enough cell material
    // or enough food and enough cell material un surrounding pixels
    if ((s > 0.5 || y > 0.5) && f > 0.8){
        y += 0.1;
    }

    // Cell eats food
    if (y > 0.5){
        f -= 0.01;
    }

    // Cell dies when not enough food or to many surrounding cells
    // Similar rule to game of life
    if (f < 0.5 || s > 0.99){
        y -= y * 0.1;

    }

    // Food regrows
    f += 0.002;

    // Mouse interaction
    if (distance(iMouse.xy, fragCoord) < 15. && iMouse.z > 0.){
        f = rand(uv+vec2(0.2));
        y = rand(uv);
    }

    // Init
    if (iFrame < 1){
        y = rand(uv)*0.9;
        f = 0.8;
    }

    fragColor = vec4(y, 0, f, 1.);
}

