#define mouse iMouse 



#define rot(a) mat2(cos(a), sin(a), -sin(a), cos(a))
#define EPS 0.0001

vec3 lightDir = normalize(vec3(1.,1., 1));

float dRing(vec3 p, float exR, float inR, float thick){
	float sdf2d = abs(length(p.xy)-exR)-inR;
	float d = abs(p.z)-thick;
	return max(sdf2d, d);
}
float map(vec3 p){
	vec3 q=p,r = p;
	p.xy *= rot(time*.5);
	p.xz *= rot(time*.3);
	float ring1 = dRing(p, .8, .09, .1);
	q.xy *= rot(time*-1.3);
	q.xz *= rot(time*-1.);
	float ring2 = dRing(q, .62, .07, .1);
	r.yz *= rot(time*2.);
	r.xz *= rot(time*-2.);
	float ring3 = dRing(r, .47, .06, .1);
	return min(ring1, min(ring2, ring3));
}

vec3 getNormal(vec3 p){
	return normalize(vec3(
		map(p)-map(vec3(p.x-EPS, p.yz)),
		map(p)-map(vec3(p.x, p.y-EPS, p.z)),
		map(p)-map(vec3(p.xy, p.z-EPS))
		));
}


vec4 render(vec2 coord ) {

	vec2 uv = (gl_FragCoord.xy*2.-resolution.xy)/min(resolution.x, resolution.y);
	vec3 col = vec3(.1);
	vec3 ro = vec3(0.,0,10.);
	vec3 rd = normalize(vec3(uv, 0)-ro);
	vec3 cur = ro;

	for(int i = 0; i <99;i++){
		float d = map(cur);
		if(d < EPS){
			vec3 normal = getNormal(cur);
			col = vec3(.6, .68, .8);
			col+= dot(normal, lightDir)+vec3(.1);
			break;
		}
		cur += rd*d;
	}

	return vec4( col, 1.0 );

}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    fragColor = render(fragCoord);
}

