const float RADIUS = 0.75;

const vec3 LINE_COLOR = vec3(1.0);
const vec3 BACK_COLOR = vec3(0.0);

const int GEN_LENGTH = 8;
vec2[GEN_LENGTH] generators() {
    float year = floor(iDate.x + iTime);
    float month = floor(iDate.y + iTime);
    float day = floor(iDate.z + iTime);
    float hour = floor(floor(iDate.w / 3600.0) + iTime);
    float minute = floor(floor((iDate.w - hour * 3600.0) / 60.0) + iTime);

    return vec2[](
        vec2(year, day),
        vec2(month, year),
        vec2(month, day),
        vec2(hour, minute),
        vec2(year, minute),
        vec2(month, hour),
        vec2(minute, month),
        vec2(year, hour)
    );
}

float random(in vec2 c) {
    const vec3 gen = vec3(114.514, 191.9810, 334.8040);
    return fract(gen.x * sin(dot(gen.yz, c)));
}

vec4 drawLine(in vec2 p, in vec2 q, in vec2 uv, in vec4 fragColor) {
    vec2 a = q - p;
    vec2 b = uv - p;
    float t = dot(b, a) / dot(a, a);
    if (t < 0.0 || 1.0 < t) return fragColor;
    vec2 h = b - t * a;
    float irr = clamp(1.0 - dot(h, h) / (THICK * THICK), 0.0, 1.0);
    return max(fragColor, vec4(irr * LINE_COLOR, 1.0));
}

struct Circle {
    vec2 center;
    float radius;
};

Circle newCircle(in vec2 center, in float radius) {
    Circle c;
    c.center = center;
    c.radius = radius;
    return c;
}

vec4 drawCircle(in Circle circle, in vec2 uv, in vec4 fragColor) {
    float dist = distance(uv, circle.center);
    float irr = (dist - circle.radius) / THICK;
    irr *= irr;
    irr = clamp(1.0 - irr, 0.0, 1.0);
    return max(fragColor, vec4(LINE_COLOR * irr, 1.0));
}

vec4 fillCircle(in Circle circle, in vec2 uv, in vec4 fragColor) {
    float dist = distance(uv, circle.center);
    if (dist < circle.radius) return vec4(BACK_COLOR, 1.0);
    else return fragColor;
}

struct Polygon {
    vec2 center;
    float radius;
    float rotation;
    int n_gon;
};

Polygon newPolygon(vec2 center, float radius, float rotation, int n_gon) {
    Polygon poly;
    poly.center = center;
    poly.radius = radius;
    poly.rotation = rotation;
    poly.n_gon = n_gon;
    return poly;
}

vec4 drawPolygon(in Polygon poly, in vec2 uv, in vec4 fragColor) {
    for (int i = 0; i < poly.n_gon; i++) {
        float t0 = poly.rotation + float(i) / float(poly.n_gon) * 2.0 * PI;
        vec2 p0 = poly.center + poly.radius * vec2(cos(t0), sin(t0));
        float t1 = poly.rotation + float(i + 1) / float(poly.n_gon) * 2.0 * PI;
        vec2 p1 = poly.center + poly.radius * vec2(cos(t1), sin(t1));
        fragColor = drawLine(p0, p1, uv, fragColor);
    }
    return fragColor;
}

vec4 fillPolygon(in Polygon poly, in vec2 uv, in vec4 fragColor) {
    int counter = 0;
    for (int i = 0; i < poly.n_gon; i++) {
        float t0 = poly.rotation + float(i) / float(poly.n_gon) * 2.0 * PI;
        vec2 p0 = poly.center + poly.radius * vec2(cos(t0), sin(t0));
        float t1 = poly.rotation + float(i + 1) / float(poly.n_gon) * 2.0 * PI;
        vec2 p1 = poly.center + poly.radius * vec2(cos(t1), sin(t1));
        vec2 a = p0 - uv;
        vec2 b = p1 - uv;
        if (a.y >= 0.0 && b.y < 0.0) {
            float x = a.x - a.y / b.y * b.x;
            if (x > 0.0) counter -= 1;
        } else if (a.y <= 0.0 && b.y > 0.0) {
            float x = a.x - a.y / b.y * b.x;
            if (x > 0.0) counter += 1;
        }
    }
    if (counter > 0) return vec4(BACK_COLOR, 1.0);
    else return fragColor;
}

vec4 drawRadial(in Polygon poly, in vec2 uv, in vec4 fragColor) {
    for (int i = 0; i < poly.n_gon; i++) {
        float t = poly.rotation + float(i) / float(poly.n_gon) * 2.0 * PI;
        vec2 p = poly.center + poly.radius * vec2(cos(t), sin(t));
        fragColor = drawLine(poly.center, p, uv, fragColor);
    }
    return fragColor;
}

vec4 drawPartialRadial(in Polygon poly, in float inner_radius, in vec2 uv, in vec4 fragColor) {
    for (int i = 0; i < poly.n_gon; i++) {
        float t = poly.rotation + float(i) / float(poly.n_gon) * 2.0 * PI;
        vec2 p = poly.center + inner_radius * vec2(cos(t), sin(t));
        vec2 q = poly.center + poly.radius * vec2(cos(t), sin(t));
        fragColor = drawLine(p, q, uv, fragColor);
    }
    return fragColor;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {    
    vec2 generators[GEN_LENGTH] = generators();

    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    fragColor = vec4(BACK_COLOR, 1.0);

    Circle c0 = newCircle(vec2(0.0), RADIUS);
    fragColor = drawCircle(c0, uv, fragColor);

    int n = int(4.0 + 4.999 * random(generators[0])), n0;
    Polygon poly = newPolygon(vec2(0.0), RADIUS, 0.0, n);
    fragColor = drawPolygon(poly, uv, fragColor);
    fragColor = drawRadial(poly, uv, fragColor);

    float r = random(generators[1]);
    if (poly.n_gon % 2 == 0) {
        n0 = 0.6 < r ? 6 : 0.2 < r ? 4 : 2;
        Polygon poly = newPolygon(vec2(0.0), RADIUS, 0.0, n0);
        fragColor = fillPolygon(poly, uv, fragColor);
        fragColor = drawPolygon(poly, uv, fragColor);
        fragColor = drawRadial(poly, uv, fragColor);
    } else {
        n0 = r < 0.5 ? 4 : 6;
        Polygon poly = newPolygon(vec2(0.0), RADIUS, 0.0, n0);
        fragColor = fillPolygon(poly, uv, fragColor);
        fragColor = drawPolygon(poly, uv, fragColor);
    }

    r = random(generators[2]);
    if (r < 0.5) {
        float r = random(generators[3]);
        if (r < 0.5) {
            Polygon poly = newPolygon(vec2(0.0), RADIUS * 0.625, 0.0, n + 4);
            fragColor = drawRadial(poly, uv, fragColor);
            poly.radius = RADIUS * 0.5;
            fragColor = fillPolygon(poly, uv, fragColor);
            fragColor = drawPolygon(poly, uv, fragColor);
        } else if (n > 5) {
            Polygon poly = newPolygon(vec2(0.0), RADIUS * 0.625, 0.0, n - 2);
            fragColor = drawRadial(poly, uv, fragColor);
            poly.radius = RADIUS * 0.25;
            fragColor = fillPolygon(poly, uv, fragColor);
            fragColor = drawPolygon(poly, uv, fragColor);
        }
    }

    r = random(generators[4]);
    if (r < 0.6) {
        Circle c = newCircle(vec2(0.0), RADIUS * 0.6875);
        fragColor = drawCircle(c, uv, fragColor);
        float r = random(generators[5]);
        if (n % 2 == 0) {
            float r7 = 7.0 * r;
            n0 = 5.0 < r7 ? 8 : 3.0 < r ? 6 : 1.0 < r7 ? 4 : 2;
            Polygon poly = newPolygon(vec2(0.0), RADIUS / 1.5, 0.0, n0);
            fragColor = drawPolygon(poly, uv, fragColor);
        } else {
            float r3 = 3.0 * r;
            n0 = 2.0 < r3 ? 3 : 1.0 < r3 ? 5 : 7;
            Polygon poly = newPolygon(vec2(0.0), RADIUS / 1.5, PI, n0);
            fragColor = drawPolygon(poly, uv, fragColor);
        }
    }

    r = random(generators[6]);
    if (r < 0.25) {
        for (int i = 0; i < n0; i++) {
            float theta = float(i) / float(n0) * 2.0 * PI;
            vec2 center = vec2(cos(theta), sin(theta)) * 11.0 / 18.0 * RADIUS;
            Circle c = newCircle(center, RADIUS * 3.0 / 22.0);
            fragColor = fillCircle(c, uv, fragColor);
            fragColor = drawCircle(c, uv, fragColor);
        }
    } else if (r < 0.5) {
        for (int i = 0; i < n0; i++) {
            float theta = float(i) / float(n0) * 2.0 * PI;
            vec2 center = vec2(cos(theta), sin(theta)) * RADIUS;
            Circle c = newCircle(center, RADIUS * 3.0 / 22.0);
            fragColor = fillCircle(c, uv, fragColor);
            fragColor = drawCircle(c, uv, fragColor);
        }
    } else if (r < 0.75) {
        Circle c = newCircle(vec2(0.0), RADIUS / 3.0);
        fragColor = drawCircle(c, uv, fragColor);
        c.radius = 3.0 / 11.0 * RADIUS;
        fragColor = fillCircle(c, uv, fragColor);
        fragColor = drawCircle(c, uv, fragColor);
    } else {
        Polygon poly = newPolygon(vec2(0.0), RADIUS, 0.0, n0);
        fragColor = drawPartialRadial(poly, RADIUS / 1.5, uv, fragColor);
        if (n != n0) {
            Circle c = newCircle(vec2(0.0), RADIUS / 1.5);
            fragColor = fillCircle(c, uv, fragColor);
            fragColor = drawCircle(c, uv, fragColor);
            r = random(generators[7]);
            int n = r < 0.25 ? 3 : r < 0.5 ? 4 : r < 0.75 ? 5 : 6;
            Polygon poly = newPolygon(vec2(0.0), RADIUS * 1.25, 0.0, n);
            fragColor = drawPolygon(poly, uv, fragColor);
            poly = newPolygon(vec2(0.0), RADIUS / 1.5, PI, n);
            fragColor = drawPolygon(poly, uv, fragColor);
        }
    }

}

